import React, { useEffect, useState } from "react";
import { MDBDataTableV5 } from "mdbreact";
// import Select from "react-select";

export default function NetworkTopUsers(ac) {
  const [showtable, setshowtable] = useState(false);
  const [trigger, settrigger] = useState(false);
  const [trigger2, settrigger2] = useState(false);
  const [loading, setloading] = useState(false);
  const [alert, setalert] = useState(false);
  const [ROW1, setROW1] = useState([]);
  const [ROW2, setROW2] = useState([]);
  const [ROW3, setROW3] = useState([]);
  const [mapROW1, setmapROW1] = useState([]);
  const [mapROW2, setmapROW2] = useState([]);
  const [mapROW3, setmapROW3] = useState([]);

  console.log("NetworkTopUsers -> database", ac.dc.datab);

  const APIbody2 = {
    "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
    ORG_ID: `${ac.dc.organizationID}`,
    ORG_LIST: ac.dc.organizationList,
    // ORG_LIST: `${organizationList}`,
    NETWORK_ID: `${ac.dc.networkID}`,
    "X-CSRFToken": "frollo",
    ARG_ORGNAME: `${ac.dc.organization}`,
    NETWORK_NAME: `${ac.dc.network}`,
    NETWORK_LIST: ac.dc.networkList,
  };

  useEffect(() => {
    fetch("/topuserdata/", {
      method: ["POST"],
      cache: "no-cache",
      headers: {
        content_type: "application/json",
      },
      body: JSON.stringify(APIbody2),
    }).then((response) => {
      return response.json;
    });
    fetch("/index/")
      .then((res) => res.json())
      .then((database) => {
        ac.dc.setdatab(database.database);
      });
  }, []);

  // const ORGANIZATIONS = ac.dc.organizationList.map((opt, index) => ({
  //   label: opt.name,
  //   value: index,
  //   id: opt.id,
  // }));

  // const NETWORKS = ac.dc.networkList.map((opt, index) => ({
