  //   id: opt.id,
  // }));

  // const DEV = Object.values(ac.dc.database);
  // const DEVICES = DEV[0].map((opt, index) => ({
  //   label: opt[0],
  //   value: index,
  // }));

  const HandleOrganization = (opt) => {
    ac.dc.setorganization(opt.label);
    ac.dc.setorganizationID(opt.id);
    ac.dc.setnetwork("Networks");
  };
  const HandleNetwork = (opt) => {
    ac.dc.setnetwork(opt.label);
    ac.dc.setnetworkID(opt.id);
  };

  const APIbody = {
    "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
    ORG_ID: `${ac.dc.organizationID}`,
    NETWORK_ID: `${ac.dc.networkID}`,
    "X-CSRFToken": "frollo",
    ARG_ORGNAME: `${ac.dc.organization}`,
    NETWORK_NAME: `${ac.dc.network}`,
    NETWORK_LIST: `${ac.dc.networkList}`,
  };

  useEffect(() => {
    async function APIcallRun() {
      try {
        if (trigger === false) {
          return;
        }
        setloading(true);

        fetch("/run/")
          .then((res) => res.json())
          .then((reports) => {
            ac.dc.setreports(reports.reports);
          })
          .then(() => setloading(false));
      } catch (err) {
        if (err) {
          setalert(true);
        }
      }
    }

    APIcallRun();
  }, [trigger]);

  const handleResults = (e) => {
    e.preventDefault();

    let R_obj1 = {};
    for (var x = 0; x < ac.dc.reports.length; x++) {
      R_obj1[x] = ac.dc.reports[x];
    }
    setROW1(R_obj1[0]);
    setROW2(R_obj1[1]);
    setROW3(R_obj1[2]);

    let R1 = [];
    ROW1.map((item) => {
      var rowModel = [
        {
          TotalusagekB: item[0],
          DownloadkB: item[1],
          UploadkB: item[2],
          Description: item[3],
          DHCPhostname: item[4],
          MACaddress: item[5],
          IPaddress: item[6],
          VLAN: item[7],
        },
      ];
      R1.push(...rowModel);
      setmapROW1(R1);
    });

    let R2 = [];
    ROW2.map((item) => {
      var rowModel = [
        {
          TotalusagekB: item[0],
          DownloadkB: item[1],
          UploadkB: item[2],
          Description: item[3],
          DHCPhostname: item[4],
          MACaddress: item[5],
          IPaddress: item[6],
          VLAN: item[7],
        },
      ];
      R2.push(...rowModel);
      setmapROW2(R2);
    });

    let R3 = [];
    ROW3.map((item) => {
      var rowModel = [
        {
          TotalusagekB: item[0],
          DownloadkB: item[1],
          UploadkB: item[2],
          Description: item[3],
          DHCPhostname: item[4],
          MACaddress: item[5],
          IPaddress: item[6],
          VLAN: item[7],
        },
      ];
      R3.push(...rowModel);
      setmapROW3(R3);
    });
    setshowtable(true);
  };

