import React, { useEffect, useState, useRef } from "react";
import { MDBDataTableV5 } from "mdbreact";

export default function GetAllDevicesIP(ac) {
  const [showtable, setshowtable] = useState(false);
  const [trigger, settrigger] = useState(0);
  const [loading, setloading] = useState(false);
  const [mapRows, setmapRows] = useState([]);

  // eslint-disable-next-line
  const [alert, setalert] = useState(false);

  const APIbody = {
    "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
    organizationId: `${ac.dc.organizationID}`,
    networkId: `${ac.dc.networkID}`,
  };

  const prevmapRows = usePrevious(mapRows);

  console.log("APIcall -> clientList", ac.dc.clientList);

  function usePrevious(value) {
    const ref = useRef();
    useEffect(() => {
      ref.current = value;
    });
    return ref.current;
  }

  useEffect(() => {
    fetch("/devices", {
      method: ["POST"],
      cache: "no-cache",
      headers: {
        content_type: "application/json",
      },
      body: JSON.stringify(APIbody),
    }).then((response) => {
      return response.json;
    });
    fetch("/devices")
      .then((res) => res.json())
      .then((client) => {
        ac.dc.setclientList(client.devices);
      });
  }, []);

  const isFirstRun = useRef(true);
  useEffect(() => {
    if (isFirstRun.current) {
      isFirstRun.current = false;
      return;
    }
    async function APIcall() {
      try {
        setloading(true);
        fetch("/devices", {
          method: ["POST"],
          cache: "no-cache",
          headers: {
            content_type: "application/json",
          },
          body: JSON.stringify(APIbody),
        }).then((response) => {
          return response.json;
        });
        fetch("/devices")
          .then((res) => res.json())
          .then((client) => {
            ac.dc.setclientList(client.devices);
          })
          .then(() => {
            let row = [];
            // eslint-disable-next-line
            ac.dc.clientList.map((item) => {
              var rowModel = [
                {
                  Description: item.name,
                  Model: item.model,
                  LAN_IP_address: item.lanIp,
                  MAC_address: item.mac,
                  WAN_1_IP: item.wan1Ip,
                  WAN_2_IP: item.wan2Ip,
                  Serial: item.serial,
                },
              ];
              row.push(...rowModel);
              setmapRows(row);
            });
          })
          // .then(() => {
          //   if (mapRows.length === 0) {
          //     console.log("APIcall -> mapRows.length", mapRows.length);
          //     settrigger(trigger + 1);
          //   }
          // })
          .then(() => setloading(false))
          .then(() => setshowtable(true));
      } catch (err) {
        if (err) {
          setalert(true);
        }
      }
    }

    APIcall();
    // eslint-disable-next-line
  }, [trigger]);

  const handleIPs = (e) => {
    e.preventDefault();
    settrigger(trigger + 1);
  };

  // const handleResults = (e) => {
  //   e.preventDefault();
  //   let row = [];
  //   ac.dc.clientList.map((item) => {
  //     var rowModel = [
  //       {
  //         Description: item.name,
  //         Model: item.model,
  //         LAN_IP_address: item.lanIp,
  //         MAC_address: item.mac,
  //         WAN_1_IP: item.wan1Ip,
  //         WAN_2_IP: item.wan2Ip,
  //         Serial: item.serial,
  //       },
  //     ];
  //     row.push(...rowModel);
  //     setmapRows(row);
  //   });
  //   // setshowtable(true);
  // };

  const datatable = {
    columns: [
      {
        label: "Description",
        field: "Description",
        width: 150,
        attributes: {
          "aria-controls": "DataTable",
          "aria-label": "Description",
        },
      },
      {
        label: "Model",
        field: "Model",
        sort: "asc",
        width: 270,
      },
      {
        label: "LAN IP address",
        field: "LAN_IP_address",
        sort: "asc",
        width: 200,
      },
      {
        label: "MAC address",
        field: "MAC_address",
        sort: "asc",
        width: 100,
      },
      {
        label: "WAN 1 IP",
        field: "WAN_1_IP",
        sort: "asc",
        width: 150,
      },
      {
        label: "WAN 2 IP",
        field: "WAN 2 IP",
        sort: "asc",
        width: 100,
      },
      {
        label: "Serial",
        field: "Serial",
        sort: "asc",
        width: 100,
      },
    ],
    rows: mapRows,
  };

  return (
    <div id="page-inner-main-templates">
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            <div className="panel-body">
              <div className="panel-group" id="accordion">
                <div className="panel panel-default">
                  <div className="panel-heading">
                    <h4 className="panel-title-description">
                      <a
                        data-toggle="collapse"
                        data-parent="#accordion"
                        href="#collapseOne"
                        className="collapsed"
                      >
                        Description
                      </a>
                    </h4>
                  </div>
                  <div id="collapseOne" className="panel-collapse collapse">
                    <div className="panel-body">
                      This scripts iterates through all networks in an
                      organization and returns all the IPs associated with every
                      organization.
                    </div>
                  </div>
                </div>
              </div>
              <button
                className="btn btn-primary"
                onClick={!loading ? handleIPs : null}
                disabled={loading}
              >
                {loading && (
                  <i
                    className="fa fa-refresh fa-spin"
                    style={{ marginRight: "5px" }}
                  />
                )}
                {loading && <span>Loading Data</span>}
                {!loading && <span>RUN</span>}
              </button>

              {/* <a
                href="#null"
                className="btn btn-primary"
                // onClick={handleResults}
              >
                Show results
              </a> */}
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            {showtable ? (
              <div className="panel-body">
                <MDBDataTableV5
                  hover
                  entriesOptions={[10, 25, 50, 100]}
                  entries={10}
                  pagesAmount={10}
                  data={datatable}
                  pagingTop
                  searchTop
                  searchBottom={false}
                  exportToCSV={true}
                />
              </div>
            ) : (
              <div></div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
