import React, { useEffect, useState, useRef } from "react";
import "../../styles/FindPorts.css";

export default function NetworkTopUsers(ac) {
  const [showtable, setshowtable] = useState(false);
  const [trigger, settrigger] = useState(0);
  const [findPort, setfindPort] = useState([]);
  const [macAddress, setmacAddress] = useState("");
  console.log("NetworkTopUsers -> macAddress", macAddress);

  const APIbody2 = {
    "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
    "X-CSRFToken": "frollo",
    ORG_ID: `${ac.dc.organizationID}`,
    MAC_ADDR: `${macAddress}`,
  };

  const isFirstRun = useRef(true);
  useEffect(() => {
    if (isFirstRun.current) {
      isFirstRun.current = false;
      return;
    }
    async function APICallReports() {
      if (ac.dc.isOrgSelected && ac.dc.isNetSelected === true) {
        try {
          ac.dc.setloadingButton(true);

          fetch("/find_ports", {
            method: ["POST"],
            cache: "no-cache",
            headers: {
              content_type: "application/json",
            },
            body: JSON.stringify(APIbody2),
          })
            .then((response) => response.json())
            .then((data) => setfindPort(data.data))

            .then(() => ac.dc.setloadingButton(false))
            .then(() => setshowtable(true));
        } catch (err) {
          if (err) {
            console.log(err);
          }
        }
      } else {
        ac.dc.setswitchAlertModal(true);
        ac.dc.setswitchToolsTemplate(false);
      }
    }
    APICallReports();
    return () => {
      setfindPort([]);
      setshowtable(false);
    };
    // eslint-disable-next-line
  }, [trigger]);

  const handleTopUsers = (e) => {
    e.preventDefault();
    settrigger(trigger + 1);
    setshowtable(true);
  };

  // const HandleOrganization = (opt) => {
  //   ac.dc.setorganization(opt.label);
  //   ac.dc.setorganizationID(opt.id);
  //   ac.dc.setnetwork("Networks");
  // };

  // const HandleNetwork = (opt) => {
  //   ac.dc.setnetwork(opt.label);
  //   ac.dc.setnetworkID(opt.id);
  // };

  // VALIDATION FORM

  const mac_add = document.getElementById("mac-address");
  const form = document.getElementById("form");
  const errorElement = document.getElementById("error");

  form.addEventListener("submit", (e) => {
    let messages = [];
    if (mac_add.value === "" || mac_add.value === null) {
      messages.push("Please insert a valid MAC Address");
    }

    if (messages.length > 0) {
      e.preventDefault();
      errorElement.innerText = messages.join(", ");
    }
  });

  return (
    <div id="page-inner-main-templates">
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            <div className="panel-body">
              <div className="panel-group" id="accordion">
                <div className="panel panel-default">
                  <div className="panel-heading">
                    <h4 className="panel-title-description">
                      <a
                        data-toggle="collapse"
                        data-parent="#accordion"
                        href="#collapseOne"
                        className="collapsed"
                      >
                        Description
                      </a>
                    </h4>
                  </div>
                  <div id="collapseOne" className="panel-collapse collapse">
                    <div className="panel-body">
                      This script finds all MS switchports that match the input
                      search parameter, searching either by clients from a file
                      listing MAC addresses (one per line), a specific tag in
                      Dashboard currently applied to ports, or the specific
                      access policy currently configured.
                    </div>
                  </div>
                </div>
              </div>
              <div className="d-flex"></div>
            </div>
            <div className="panel-body">
              <form id="form">
                <div id="error" class="form-group">
                  <label class="control-label" for="input_1">
                    Text 1
                  </label>
                  <div class="form-input-error-msg alert alert-danger">
                    <span class="glyphicon glyphicon-exclamation-sign"></span>{" "}
                    This field is mandatary
                  </div>
                  <input
                    type="text"
                    id="mac-address"
                    class="form-control validate"
                    onChange={(e) => setmacAddress(e.target.value)}
                  />
                </div>

                <button id="submit" class="btn btn-primary btn-lg">
                  Sumbit
                </button>
              </form>
              <button
                id="runButton"
                type="submit"
                className="btn btn-primary"
                onClick={!ac.dc.loadingButton ? handleTopUsers : null}
                disabled={ac.dc.loadingButton}
              >
                {ac.dc.loadingButton && (
                  <i
                    className="fa fa-refresh fa-spin"
                    style={{ marginRight: "5px" }}
                  />
                )}
                {ac.dc.loadingButton && <span>Loading Data</span>}
                {!ac.dc.loadingButton && <span>RUN</span>}
              </button>
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            {showtable ? (
              <div className="panel-body">
                <div className="table-responsive table-bordered">
                  <table className="table">
                    <thead>
                      <tr>
                        <th>#</th>
                        <th>Switch</th>
                        <th>Port</th>
                        <th>IP Address</th>
                        <th>MAC Address</th>
                      </tr>
                    </thead>
                    <tbody>
                      <tr>
                        <td>1</td>
                        <td>{findPort[0]}</td>
                        <td>{findPort[1]}</td>
                        <td></td>
                        <td></td>
                      </tr>
                    </tbody>
                  </table>
                </div>
              </div>
            ) : (
              <div></div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
