import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Template from "./components/Template";
import LoginAPI from "./components/LoginAPI";
import Dashboard from "./components/Dashboard";

const MainContext = React.createContext(null);

function App() {
  const [organization, setorganization] = useState(0);
  const [organizationID, setorganizationID] = useState(0);
  const [timeZone, settimeZone] = useState(0);
  const [network, setnetwork] = useState(0);

  useEffect(() => {
    fetch("/organizations")
      .then((res) => res.json())
      .then((organizations) => {
        setorganization(organizations.organizations[0].name);
        setorganizationID(organizations.organizations[0].id);
      });
  }, []);

  useEffect(() => {
    fetch("/networks")
      .then((res) => res.json())
      .then((network) => {
        setnetwork(network.networks[0].name);
        settimeZone(network.networks[0].timeZone);
      });
  }, []);

  const dc = {
    organization,
    setorganization,
    organizationID,
    setorganizationID,
    timeZone,
    settimeZone,
    network,
    setnetwork,
  };

  return (
    <MainContext.Provider dc={dc}>
      <Router>
        <Switch>
          <Route path="/dashboard" exact component={Template} />
          <Route path="/login" render={(ac) => <LoginAPI {...ac} dc={dc} />} />
          <Route
            path="/dashboard"
            render={(ac) => <Dashboard {...ac} dc={dc} />}
          />
        </Switch>
      </Router>
    </MainContext.Provider>
  );
}

export default App;
