import React, { useEffect, useState, useRef } from "react";
import myLogs from "../DebugsLogs/debug.log";
import "../styles/LiveLog.css";

export default function LiveLog(ac) {
  const [trigger, settrigger] = useState(0);

  const isFirstRun = useRef(true);
  useEffect(() => {
    if (isFirstRun.current) {
      isFirstRun.current = false;
      return;
    }
    async function APICallLog() {
      try {
        fetch(myLogs)
          .then((response) => {
            return response.text();
          })
          .then((data) => {
            document.getElementById("log").innerHTML = data;
          })
          .then(() => {
            var logBody = document.getElementById("log");
            logBody.scrollTop = logBody.scrollHeight;
          });
      } catch (err) {
        if (err) {
          console.log(err);
          ac.dc.setalert(true);
        }
      }
    }
    APICallLog();
  }, [trigger]);

  const toggle = () => {
    settrigger(trigger + 1);
  };

  // function updateScroll() {
  //   var logBody = document.getElementById("log");
  //   logBody.scrollTop = logBody.scrollHeight;
  // }

  // // once a second
  // setInterval(updateScroll, 1000);

  return (
    <div>
      <button
        onClick={toggle}
        href="#myModal"
        id="openBtn"
        data-toggle="modal"
        class="btn btn-primary"
      >
        Logs
      </button>

      <div class="modal fade" id="myModal">
        <div class="modal-dialog modal-xl">
          <div class="modal-content-log">
            <div class="modal-header">
              <button
                type="button"
                class="close"
                data-dismiss="modal"
                aria-hidden="true"
              >
                Ã—
              </button>
            </div>
            <div id="log" class="modal-body-log"></div>
            <div class="modal-footer">
              <button
                type="button"
                class="btn btn-default "
                data-dismiss="modal"
              >
                Close
              </button>
              <button type="button" class="btn btn-primary">
                Refresh
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* <button
        onClick={toggle}
        type="button"
        className="btn btn-primary"
        data-toggle="modal"
        data-target=".bd-example-modal-xl"
      >
        Logs
      </button> */}
      {/* <div
        className="modal fade bd-example-modal-xl"
        // id="exampleModalCenter"
        tabIndex="-1"
        role="dialog"
        aria-labelledby="myExtraLargeModalLabel"
        aria-hidden="true"
      >
        <div className="modal-dialog modal-xl" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <button
                type="button"
                className="close"
                data-dismiss="modal"
                aria-label="Close"
              >
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div id="log" className="modal-body-log">
              ...
            </div>
            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-secondary"
                data-dismiss="modal"
              >
                Refresh
              </button>
            </div>
          </div>
        </div>
      </div> */}
    </div>
  );
}
