import React, { useState, useEffect } from "react";
import Template from "./components/Template";

const MainContext = React.createContext(null);

function App() {
  const InitialKey = "12345";
  const [apiKey, setapiKey] = useState(InitialKey);
  const [organizationList, setorganizationList] = useState([]);
  console.log("App -> organizationList", organizationList);
  const [networkList, setnetworkList] = useState([]);
  console.log("App -> networkList", networkList);
  const [allNetworksIDList, setallNetworksIDList] = useState([]);
  const [organization, setorganization] = useState("Set Organization");
  const [organizationID, setorganizationID] = useState(0);
  const [networkID, setnetworkID] = useState(0);
  const [network, setnetwork] = useState("Networks");
  const [deviceList, setdeviceList] = useState([]);
  const [clientList, setclientList] = useState([]);
  const [vlanList, setvlanList] = useState([]);
  const [allVlanList, setallVlanList] = useState([]);
  const [timeZone, settimeZone] = useState(0);
  const [Name, setName] = useState("");
  const [loginEmail, setloginEmail] = useState("");
  const [inputKey, setinputKey] = useState();
  const [inputConfKey, setinputConfKey] = useState();
  const [isLoggedIn, setisLoggedIn] = useState(false);
  const [switchLoginAPI, setswitchLoginAPI] = useState(true);
  const [switchDashboard, setswitchDashboard] = useState(false);
  const [switchLoggedIn, setswitchLoggedIn] = useState(false);
  const [switchLoggedout, setswitchLoggedout] = useState(false);
  const [switchToolsTemplate, setswitchToolsTemplate] = useState(false);
  const [switchMainTools, setswitchMainTools] = useState(false);
  const [switchswitchChangeApiKey, setswitchswitchChangeApiKey] = useState(
    false
  );
  const [classLogin, setclassLogin] = useState("active-menu");
  const [classDashboard, setclassDashboard] = useState("");
  const [classOrganization, setclassOrganization] = useState("");
  const [classNetwork, setclassNetwork] = useState("");
  const [classToolsTemplate, setclassToolsTemplate] = useState("");
  const [logInlogOut, setlogInlogOut] = useState("Login");
  const [ulClassorg, setulClassorg] = useState("nav nav-second-level");
  const [ulClassnet, setulClassnet] = useState("nav nav-second-level");
  const [totalDevices, settotalDevices] = useState(0);
  const [mapRows, setmapRows] = useState([]);
  const [reports, setreports] = useState([]);
  console.log("App -> reports", reports);
  const [datab, setdatab] = useState([]);
  console.log("App -> datab", datab);

  const APIbody = {
    "X-Cisco-Meraki-API-Key": `${apiKey}`,
    organizationId: `${organizationID}`,
    networkId: `${networkID}`,
  };

  const APIbody2 = {
    "X-Cisco-Meraki-API-Key": `${apiKey}`,
    ORG_ID: `${organizationID}`,
    ORG_LIST: organizationList,
    // ORG_LIST: `${organizationList}`,
    NETWORK_ID: `${networkID}`,
    "X-CSRFToken": "frollo",
    ARG_ORGNAME: `${organization}`,
    NETWORK_NAME: `${network}`,
    NETWORK_LIST: networkList,
  };

  useEffect(() => {
    const data = localStorage.getItem("my-token");
    if (data) {
      setapiKey(JSON.parse(data));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("my-token", JSON.stringify(apiKey));
  });

  useEffect(() => {
    fetch("/organizations", {
      method: ["POST"],
      cache: "no-cache",
      headers: {
        content_type: "application/json",
      },
      body: JSON.stringify(APIbody),
    }).then((response) => {
      return response.json;
    });
    fetch("/organizations")
      .then((res) => res.json())
      .then((organizations) => {
        setorganizationList(organizations.organizations);
      });
  }, [apiKey]);

  useEffect(() => {
    fetch("/networks", {
      method: ["POST"],
      cache: "no-cache",
      headers: {
        content_type: "application/json",
      },
      body: JSON.stringify(APIbody),
      // body: JSON.stringify(apiKey),
    }).then((response) => {
      return response.json;
    });
    fetch("/networks")
      .then((res) => res.json())
      .then((network) => {
        const NET = Object.values(network.networks);
        let networkIDList = [];
        NET.map((item) => {
          var IDListModel = [
            {
              id: item.id,
            },
          ];
          networkIDList.push(...IDListModel);
          setallNetworksIDList(networkIDList);
        });

        setnetworkList(network.networks);
        settimeZone(network.networks[0].timeZone);
      });
  }, [apiKey, organization]);

  useEffect(() => {
    fetch("/devices", {
      method: ["POST"],
      cache: "no-cache",
      headers: {
        content_type: "application/json",
      },
      body: JSON.stringify(APIbody),
    }).then((response) => {
      return response.json;
    });
    fetch("/devices")
      .then((res) => res.json())
      .then((device) => {
        setdeviceList(device.devices);
        settotalDevices(device.devices.length);
      });
  }, [networkID]);

  // useEffect(() => {
  //   async function APIcall() {
  //     try {
  //       fetch("/topuserdata/", {
  //         method: ["POST"],
  //         cache: "no-cache",
  //         headers: {
  //           content_type: "application/json",
  //         },
  //         body: JSON.stringify(APIbody2),
  //       }).then((response) => {
  //         return response.json;
  //       });
  //       fetch("/index/")
  //         .then((res) => res.json())
  //         .then((reports) => {
  //           setreports(reports.reports);
  //           console.log("NetworkTopUsers -> reports", reports);
  //         });
  //     } catch (err) {
  //       if (err) {
  //       }
  //     }
  //   }

  //   APIcall();
  // }, [apiKey, organization]);

  useEffect(() => {
    async function APIcallRun() {
      try {
        // if (trigger === false) {
        //   return;
        // }
        // setloading(true);
        fetch("/topuserdata/", {
          method: ["POST"],
          cache: "no-cache",
          headers: {
            content_type: "application/json",
          },
          body: JSON.stringify(APIbody2),
        }).then((response) => {
          return response.json;
        });

        // .then(() => setloading(false));
      } catch (err) {
        if (err) {
          console.log(err);
        }
      }
    }

    APIcallRun();
  }, [apiKey, organization]);

  // useEffect(() => {
  //   fetch("/topuserdata/", {
  //     method: ["POST"],
  //     cache: "no-cache",
  //     headers: {
  //       content_type: "application/json",
  //     },
  //     body: JSON.stringify(APIbody2),
  //   }).then((response) => {
  //     return response.json;
  //   });
  //   fetch("/index/")
  //     .then((res) => res.json())
  //     .then((database) => {
  //       setdatab(database.database);
  //     });
  // }, [organization]);

  useEffect(() => {
    fetch("/index/")
      .then((res) => res.json())
      .then((database) => {
        setdatab(database.database);
      });
  }, [network]);

  const dc = {
    organizationList,
    setorganizationList,
    networkList,
    setnetworkList,
    networkID,
    setnetworkID,
    organization,
    setorganization,
    organizationID,
    setorganizationID,
    timeZone,
    settimeZone,
    network,
    setnetwork,
    apiKey,
    setapiKey,
    Name,
    setName,
    loginEmail,
    setloginEmail,
    inputKey,
    setinputKey,
    inputConfKey,
    setinputConfKey,
    isLoggedIn,
    setisLoggedIn,
    switchLoginAPI,
    setswitchLoginAPI,
    switchDashboard,
    setswitchDashboard,
    switchLoggedIn,
    setswitchLoggedIn,
    switchLoggedout,
    setswitchLoggedout,
    switchToolsTemplate,
    setswitchToolsTemplate,
    classLogin,
    setclassLogin,
    classDashboard,
    setclassDashboard,
    switchswitchChangeApiKey,
    setswitchswitchChangeApiKey,
    switchMainTools,
    setswitchMainTools,
    logInlogOut,
    setlogInlogOut,
    ulClassorg,
    setulClassorg,
    ulClassnet,
    setulClassnet,
    classOrganization,
    setclassOrganization,
    classNetwork,
    setclassNetwork,
    totalDevices,
    settotalDevices,
    deviceList,
    setdeviceList,
    classToolsTemplate,
    setclassToolsTemplate,
    clientList,
    setclientList,
    mapRows,
    setmapRows,
    vlanList,
    setvlanList,
    allNetworksIDList,
    setallNetworksIDList,
    allVlanList,
    setallVlanList,
    reports,
    setreports,
    datab,
    setdatab,
  };

  return (
    <MainContext.Provider dc={dc}>
      <Template dc={dc} />
    </MainContext.Provider>
  );
}

export default App;
