import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [organization, setorganization] = useState(0);
  const [network, setnetwork] = useState(0);
  console.log("App -> network", network);

  useEffect(() => {
    fetch("/organizations")
      .then((res) => res.json())

      .then((inventory) => {
        setorganization(inventory.inventory[0].name);
        // setorganization(inventory.inventory[0].name);
      });
  }, []);

  useEffect(() => {
    fetch("/networks")
      .then((res) => res.json())

      .then((network) => {
        console.log("App -> network", network.networks[0].name);
        setnetwork(network.networks[0].name);
      });
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        ... no changes in this part ...
        <p>The current Organization is {organization}.</p>
        <p>
          Networks in {organization}: {network}.
        </p>
      </header>
    </div>
  );
}

export default App;
