import React, { useState, useEffect } from "react";
import logo from "./logo.svg";
import "./App.css";

function App() {
  const [currentOrganization, setCurrentOrganization] = useState(0);
  console.log("App -> currentOrganization", currentOrganization);

  useEffect(() => {
    fetch("/organizations")
      .then((res) => res.json())

      .then((inventory) => {
        console.log("App -> data", inventory);
        setCurrentOrganization(inventory.inventory[0].name);
      });
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        ... no changes in this part ...
        <p>The current Organization is {currentOrganization}.</p>
      </header>
    </div>
  );
}

export default App;
