import React, { useEffect, useState, useRef } from "react";
import { Button, Modal, ModalHeader, ModalBody } from "reactstrap";
import myLogs from "./debug.log";
import "../styles/LiveLog.css";

export default function LiveLog() {
  const [trigger, settrigger] = useState(0);
  const [showLiveLog, setshowLiveLog] = useState(false);

  const isFirstRun = useRef(true);
  useEffect(() => {
    if (isFirstRun.current) {
      isFirstRun.current = false;
      return;
    }
    async function APICallLog() {
      try {
        fetch(myLogs)
          .then((response) => {
            return response.text();
          })
          .then((data) => {
            document.getElementById("log").innerHTML = data;
          })
          .then(() => {
            var logBody = document.getElementById("log");
            logBody.scrollTop = logBody.scrollHeight;
          });
      } catch (err) {
        if (err) {
          console.log(err);
        }
      }
    }
    APICallLog();
  }, [trigger]);

  const toggle = () => {
    settrigger(trigger + 1);
    // setshowLiveLog(!showLiveLog);
  };

  // function updateScroll() {
  //   var logBody = document.getElementById("log");
  //   logBody.scrollTop = logBody.scrollHeight;
  // }

  // // once a second
  // setInterval(updateScroll, 1000);

  return (
    <div>
      <button
        onClick={toggle}
        type="button"
        class="btn btn-primary"
        data-toggle="modal"
        data-target="#exampleModal"
      >
        Launch Live Log
      </button>
      <div
        class="modal fade"
        id="myModal"
        tabindex="-1"
        role="dialog"
        aria-labelledby="myModalLabel"
        aria-hidden="true"
      >
        <div class="vertical-alignment-helper">
          <div class="modal-dialog vertical-align-center">
            <div class="modal-content">
              <div class="modal-header">
                <button type="button" class="close" data-dismiss="modal">
                  <span aria-hidden="true">&times;</span>
                  <span class="sr-only">Close</span>
                </button>
                <h4 class="modal-title" id="myModalLabel">
                  Modal title
                </h4>
              </div>
              <div class="modal-body">...</div>
            </div>
          </div>
        </div>
      </div>

      {/* <div
        class="modal fade"
        id="exampleModal"
        tabindex="-1"
        role="dialog"
        aria-labelledby="exampleModalLabel"
        aria-hidden="true"
      >
        <div class="modal-dialog" role="document">
          <div class="modal-content">
            <div class="modal-header">
              <button
                type="button"
                class="close"
                data-dismiss="modal"
                aria-label="Close"
              >
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div id="log" class="modal-body">
              ...
            </div>
          </div>
        </div>
      </div> */}
    </div>
  );
}
