import React, { useEffect, useState } from "react";
import { MDBDataTableV5 } from "mdbreact";
// import "@fortawesome/fontawesome-free/css/all.min.css";
// import "bootstrap-css-only/css/bootstrap.min.css";
// import "mdbreact/dist/css/mdb.css";

export default function GetAllDevicesIP(ac) {
  const [showtable, setshowtable] = useState(false);
  const [trigger, settrigger] = useState(false);
  const [loading, setloading] = useState(false);
  const [alert, setalert] = useState(false);
  const APIbody = {
    "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
    organizationId: `${ac.dc.organizationID}`,
    networkId: `${ac.dc.networkID}`,
  };

  useEffect(() => {
    async function APIcall() {
      try {
        if (trigger === false) {
          return;
        }
        setloading(true);

        fetch("/devices", {
          method: ["POST"],
          cache: "no-cache",
          headers: {
            content_type: "application/json",
          },
          body: JSON.stringify(APIbody),
        }).then((response) => {
          console.log("GetAllDevicesIP -> response", response);
          return response.json;
        });
        fetch("/devices")
          .then((res) => res.json())
          .then((client) => {
            ac.dc.setclientList(client.devices);
          });
      } catch (err) {
        if (err) {
          ac.dc.setalert(true);
        }
      }
      setloading(false);
    }
    APIcall();
  }, [trigger]);

  const handleIPs = (e) => {
    e.preventDefault();
    setshowtable(true);
    settrigger(true);
    let row = [];
    ac.dc.clientList.map((item) => {
      var rowModel = [
        {
          Description: item.name,
          Model: item.model,
          LAN_IP_address: item.lanIp,
          MAC_address: item.mac,
          WAN_1_IP: item.wan1Ip,
          WAN_2_IP: item.wan2Ip,
          Serial: item.serial,
        },
      ];
      row.push(...rowModel);
      ac.dc.setmapRows(row);
    });
  };

  const datatable = {
    columns: [
      {
        label: "Description",
        field: "Description",
        width: 150,
        attributes: {
          "aria-controls": "DataTable",
          "aria-label": "Description",
        },
      },
      {
        label: "Model",
        field: "Model",
        sort: "asc",
        width: 270,
      },
      {
        label: "LAN IP address",
        field: "LAN_IP_address",
        sort: "asc",
        width: 200,
      },
      {
        label: "MAC address",
        field: "MAC_address",
        sort: "asc",
        width: 100,
      },
      {
        label: "WAN 1 IP",
        field: "WAN_1_IP",
        sort: "asc",
        width: 150,
      },
      {
        label: "WAN 2 IP",
        field: "WAN 2 IP",
        sort: "asc",
        width: 100,
      },
      {
        label: "Serial",
        field: "Serial",
        sort: "asc",
        width: 100,
      },
    ],
    rows: ac.dc.mapRows,
  };

  return (
    <div id="page-inner-main-templates">
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            <div className="panel-body">
              <div className="panel panel-success">
                <div className="panel-heading">Description</div>
                <div className="panel-body">
                  <p>
                    This scripts iterates through all networks in an
                    organization and returns all the IPs associated with every
                    organization.
                  </p>
                </div>
                <a href="#null" className="btn btn-primary" onClick={handleIPs}>
                  RUN
                </a>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            {showtable ? (
              <div className="panel-body">
                <MDBDataTableV5
                  hover
                  entriesOptions={[10, 25, 50, 100]}
                  entries={10}
                  pagesAmount={10}
                  data={datatable}
                  pagingTop
                  searchTop
                  searchBottom={false}
                  exportToCSV={true}
                />
              </div>
            ) : (
              <div></div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
