import React, { useState } from "react";
import MainTools from "./Tools/MainTools";
import "../styles/ToolsTemplate.css";

const ToolsContext = React.createContext(null);

export default function ToolsTemplate(ac) {
  const [switchGetAllDevicesIP, setswitchGetAllDevicesIP] = useState(false);
  const [switchGetAllSubnets, setswitchGetAllSubnets] = useState(false);
  const [switchAllTools, setswitchAllTools] = useState({ 1: false, 2: false });
  console.log("ToolsTemplate -> switchAllTools", switchAllTools);

  const shutAllTools = () => {
    setswitchAllTools({ 1: true });
  };

  const handleMainTools = () => {
    ac.dc.setswitchMainTools(true);
    let selectBox = document.getElementById("selectBox");
    let selectedValue = selectBox.options[selectBox.selectedIndex].value;
    if (selectedValue === "1") {
      setswitchGetAllDevicesIP(true);
      setswitchGetAllSubnets(false);
    } else if (selectedValue === "2") {
      setswitchGetAllSubnets(true);
      setswitchGetAllDevicesIP(false);
    }
    // alert(selectedValue);
  };

  const ts = {
    switchGetAllDevicesIP,
    setswitchGetAllDevicesIP,
    switchGetAllSubnets,
    setswitchGetAllSubnets,
  };
  return (
    <div id="page-inner-tool-templates">
      <div className="row">
        <div className="col-md-3 col-sm-12 col-xs-12">
          <div className="board">
            <div className="panel panel-primary">
              <div className="number">
                <h3 className="h3-dashboard">{ac.dc.network}</h3>
                <small>Network</small>
              </div>
              <div className="icon">
                <i className="fa fa-eye fa-5x red"></i>
              </div>
            </div>
          </div>
        </div>

        <div className="col-md-3 col-sm-12 col-xs-12">
          <div className="board">
            <div className="panel panel-primary">
              <div className="number">
                <h3 className="h3-dashboard">{ac.dc.organizationID}</h3>
                <small>Organization ID</small>
              </div>
              <div className="icon">
                <i className="fa fa-shopping-cart fa-5x blue"></i>
              </div>
            </div>
          </div>
        </div>

        <div className="col-md-3 col-sm-12 col-xs-12">
          <div className="board">
            <div className="panel panel-primary">
              <div className="number">
                <h3 className="h3-dashboard">{ac.dc.timeZone}</h3>
                <small>Time Zone</small>
              </div>
              <div className="icon">
                <i className="fa fa-comments fa-5x green"></i>
              </div>
            </div>
          </div>
        </div>

        <div className="col-md-3 col-sm-12 col-xs-12">
          <div className="board">
            <div className="panel panel-primary">
              <div className="number">
                <h3 className="h3-dashboard">{ac.dc.totalDevices}</h3>
                <small>Total Devices</small>
              </div>
              <div className="icon">
                <i className="fa fa-user fa-5x yellow"></i>
              </div>
            </div>
          </div>
        </div>
        <div className="col-xs-12">
          <div className="panel panel-default">
            <div className="panel-body">
              <div className="sub-title-tools">Select Tools</div>
              <div>
                <select
                  onChange={handleMainTools}
                  id="selectBox"
                  className="btn btn-default dropdown-toggle-tools"
                >
                  <optgroup label="Tshoot Tools">
                    <option className="option-tools-disabled" value="0">
                      Select Tools
                    </option>
                    <option value="1">Get all devices IPs</option>
                    <option value="2">Get all subnets</option>
                    <option value="3">Top 10 talkers</option>
                  </optgroup>
                </select>
              </div>
            </div>
          </div>
        </div>
      </div>
      <ToolsContext.Provider ts={ts}>
        {ac.dc.switchMainTools ? <MainTools dc={ac.dc} ts={ts} /> : <div></div>}
      </ToolsContext.Provider>
    </div>
  );
}
