import React from "react";
import "../styles/LoginAPI.css";

export default function LoginAPI(ac) {
  const handleLogin = () => {
    if (ac.dc.inputKey === ac.dc.inputConfKey) {
      var APIobj = JSON.parse(
        `{ "X-Cisco-Meraki-API-Key":"${ac.dc.inputConfKey}" }`
      );
      ac.dc.setapiKey(APIobj);
      ac.dc.setisLoggedIn(true);
      ac.dc.setswitchLoggedIn(true);
      ac.dc.setloginName(ac.dc.loginName);
      ac.dc.setloginEmail(ac.dc.loginEmail);
    } else {
      alert("key mismatch");
    }
  };

  return (
    <div className="container register">
      <div className="row">
        <div className="col-md-3 register-left">
          <img src="https://image.ibb.co/n7oTvU/logo_white.png" alt="" />
          <h3>Welcome</h3>
          <p>Please set your Meraki API key</p>
          <br />
        </div>
        <div className="col-md-9 register-right">
          <div className="tab-content" id="myTabContent">
            <div>
              <div className="row register-form">
                <div className="col-md-6">
                  <form>
                    <div className="form-group">
                      <input
                        type="text"
                        className="form-control"
                        placeholder="Name *"
                        value={ac.dc.loginName}
                        onChange={(e) => ac.dc.setloginName(e.target.value)}
                      />
                    </div>
                  </form>
                  <form>
                    <div className="form-group">
                      <input
                        type="password"
                        className="form-control"
                        placeholder="API key *"
                        value={ac.dc.inputKey}
                        autoComplete="api"
                        onChange={(e) => ac.dc.setinputKey(e.target.value)}
                      />
                    </div>
                  </form>
                </div>
                <div className="col-md-6">
                  <form>
                    <div className="form-group">
                      <input
                        type="text"
                        className="form-control"
                        placeholder="E-mail *"
                        value={ac.dc.loginEmail}
                        onChange={(e) => ac.dc.setloginEmail(e.target.value)}
                      />
                    </div>
                  </form>
                  <form>
                    <div className="form-group">
                      <input
                        type="password"
                        className="form-control"
                        placeholder="Confirm API key *"
                        value={ac.dc.inputConfKey}
                        autoComplete="api"
                        onChange={(e) => ac.dc.setinputConfKey(e.target.value)}
                      />
                    </div>
                  </form>
                  <input
                    onClick={handleLogin}
                    type="submit"
                    className="btnRegister"
                    value="Login"
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
