import React, { useEffect, useState } from "react";
import { MDBDataTableV5 } from "mdbreact";
import Select from "react-select";

export default function NetworkTopUsers(ac) {
  const [showtable, setshowtable] = useState(false);
  const [trigger, settrigger] = useState(0);
  console.log("NetworkTopUsers -> trigger", trigger);
  const [alert, setalert] = useState(false);
  const [ROW1, setROW1] = useState([]);
  const [ROW2, setROW2] = useState([]);
  const [ROW3, setROW3] = useState([]);
  const [mapROW1, setmapROW1] = useState([]);
  const [mapROW2, setmapROW2] = useState([]);
  const [mapROW3, setmapROW3] = useState([]);

  const APIbody2 = {
    "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
    "X-CSRFToken": "frollo",
    ARG_ORGNAME: `${ac.dc.organization}`,
    SERIAL_NUM: `${ac.dc.SNtopUsers}`,
    NET_ID: `${ac.dc.networkID}`,
    NET_NAME: `${ac.dc.network}`,
  };

  useEffect(() => {
    try {
      // if (trigger === false) {
      //   return;
      // }
      ac.dc.setloadingButton(true);

      fetch("/topuserdata/", {
        method: ["POST"],
        cache: "no-cache",
        headers: {
          content_type: "application/json",
        },
        body: JSON.stringify(APIbody2),
      })
        .then((response) => {
          return response.json;
        })

        .then(() => ac.dc.setloadingButton(false));
    } catch (err) {
      if (err) {
        console.log(err);
      }
    }
  }, []);

  useEffect(() => {
    async function APICallReports() {
      try {
        if (trigger === false) {
          return;
        }
        ac.dc.setloadingButton(true);

        fetch("/get_report/")
          .then((res) => res.json())
          .then((reports) => {
            ac.dc.setreports(reports.reports);
          })
          .then(() => ac.dc.setloadingButton(false));
      } catch (err) {
        if (err) {
          setalert(true);
        }
      }
    }

    APICallReports();
  }, [trigger]);

  // const APIbody2 = {
  //   "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
  //   ORG_ID: `${ac.dc.organizationID}`,
  //   ORG_LIST: ac.dc.organizationList,
  //   NETWORK_ID: `${ac.dc.networkID}`,
  //   "X-CSRFToken": "frollo",
  //   ARG_ORGNAME: `${ac.dc.organization}`,
  //   NETWORK_NAME: `${ac.dc.network}`,
  //   NETWORK_LIST: ac.dc.networkList,
  // };

  const ORGANIZATIONS = ac.dc.organizationList.map((opt, index) => ({
    label: opt.name,
    value: index,
    id: opt.id,
  }));

  const NETWORKS = ac.dc.networkList.map((opt, index) => ({
    label: opt.name,
    value: index,
    id: opt.id,
  }));

  const handleTopUsers = (e) => {
    e.preventDefault();
    settrigger(trigger + 1);
  };

  const HandleOrganization = (opt) => {
    ac.dc.setorganization(opt.label);
    ac.dc.setorganizationID(opt.id);
    ac.dc.setnetwork("Networks");
  };

  useEffect(() => {
    let Dev1 = {};
    for (var device = 0; device < ac.dc.deviceList.length; device++) {
      Dev1[device] = ac.dc.deviceList[device];
      let model = Dev1[device].model;
      if (model.startsWith("MX") || model.startsWith("Z")) {
        ac.dc.setSNtopUsers(Dev1[device].serial);
      }
    }
  }, [trigger]);

  const HandleNetwork = (opt) => {
    settrigger(trigger + 1);
    ac.dc.setnetwork(opt.label);
    ac.dc.setnetworkID(opt.id);
  };

  const handleResults = (e) => {
    e.preventDefault();

    let R_obj1 = {};
    for (var x = 0; x < ac.dc.reports.length; x++) {
      R_obj1[x] = ac.dc.reports[x];
    }
    setROW1(R_obj1[0]);
    setROW2(R_obj1[1]);
    setROW3(R_obj1[2]);

    let R1 = [];
    ROW1.map((item) => {
      var rowModel = [
        {
          TotalusagekB: item[0],
          DownloadkB: item[1],
          UploadkB: item[2],
          Description: item[3],
          DHCPhostname: item[4],
          MACaddress: item[5],
          IPaddress: item[6],
          VLAN: item[7],
        },
      ];
      R1.push(...rowModel);
      setmapROW1(R1);
    });

    let R2 = [];
    ROW2.map((item) => {
      var rowModel = [
        {
          TotalusagekB: item[0],
          DownloadkB: item[1],
          UploadkB: item[2],
          Description: item[3],
          DHCPhostname: item[4],
          MACaddress: item[5],
          IPaddress: item[6],
          VLAN: item[7],
        },
      ];
      R2.push(...rowModel);
      setmapROW2(R2);
    });

    let R3 = [];
    ROW3.map((item) => {
      var rowModel = [
        {
          TotalusagekB: item[0],
          DownloadkB: item[1],
          UploadkB: item[2],
          Description: item[3],
          DHCPhostname: item[4],
          MACaddress: item[5],
          IPaddress: item[6],
          VLAN: item[7],
        },
      ];
      R3.push(...rowModel);
      setmapROW3(R3);
    });
    setshowtable(true);
  };

  const datatable_10 = {
    columns: [
      {
        label: "Total usage kB",
        field: "TotalusagekB",
        width: 150,
        attributes: {
          "aria-controls": "DataTable",
          "aria-label": "Description",
        },
      },
      {
        label: "Download kB",
        field: "DownloadkB",
        sort: "asc",
        width: 270,
      },
      {
        label: "Upload kB",
        field: "UploadkB",
        sort: "asc",
        width: 200,
      },
      {
        label: "Description",
        field: "Description",
        sort: "asc",
        width: 100,
      },
      {
        label: "DHCP hostname",
        field: "DHCPhostname",
        sort: "asc",
        width: 100,
      },
      {
        label: "MAC address",
        field: "MACaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "IP address",
        field: "IPaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "VLAN",
        field: "VLAN",
        sort: "asc",
        width: 100,
      },
    ],
    rows: mapROW1,
  };
  const datatable_30 = {
    columns: [
      {
        label: "Total usage kB",
        field: "TotalusagekB",
        width: 150,
        attributes: {
          "aria-controls": "DataTable",
          "aria-label": "Description",
        },
      },
      {
        label: "Download kB",
        field: "DownloadkB",
        sort: "asc",
        width: 270,
      },
      {
        label: "UploadkB",
        field: "VlanName",
        sort: "asc",
        width: 200,
      },
      {
        label: "Description",
        field: "Description",
        sort: "asc",
        width: 100,
      },
      {
        label: "DHCP hostname",
        field: "DHCPhostname",
        sort: "asc",
        width: 100,
      },
      {
        label: "MAC address",
        field: "MACaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "IP address",
        field: "IPaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "VLAN",
        field: "VLAN",
        sort: "asc",
        width: 100,
      },
    ],
    rows: mapROW2,
  };

  const datatable_60 = {
    columns: [
      {
        label: "Total usage kB",
        field: "TotalusagekB",
        width: 150,
        attributes: {
          "aria-controls": "DataTable",
          "aria-label": "Description",
        },
      },
      {
        label: "Download kB",
        field: "DownloadkB",
        sort: "asc",
        width: 270,
      },
      {
        label: "UploadkB",
        field: "VlanName",
        sort: "asc",
        width: 200,
      },
      {
        label: "Description",
        field: "Description",
        sort: "asc",
        width: 100,
      },
      {
        label: "DHCP hostname",
        field: "DHCPhostname",
        sort: "asc",
        width: 100,
      },
      {
        label: "MAC address",
        field: "MACaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "IP address",
        field: "IPaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "VLAN",
        field: "VLAN",
        sort: "asc",
        width: 100,
      },
    ],
    rows: mapROW3,
  };

  return (
    <div id="page-inner-main-templates">
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            <div className="panel-body">
              <div className="panel-group" id="accordion">
                <div className="panel panel-default">
                  <div className="panel-heading">
                    <h4 className="panel-title-description">
                      <a
                        data-toggle="collapse"
                        data-parent="#accordion"
                        href="#collapseOne"
                        className="collapsed"
                      >
                        Description
                      </a>
                    </h4>
                  </div>
                  <div id="collapseOne" className="panel-collapse collapse">
                    <div className="panel-body">
                      This is script gets the top 10 heaviest bandwidth users of
                      an MX security appliance for the last 10, 30 and 60
                      minutes.
                    </div>
                  </div>
                </div>
              </div>
              <div className="d-flex">
                <Select
                  options={ORGANIZATIONS}
                  placeholder={ac.dc.organization}
                  onChange={HandleOrganization}
                />
                <Select
                  options={NETWORKS}
                  placeholder={ac.dc.network}
                  onChange={HandleNetwork}
                />
                {/* <Select options={DEVICES} placeholder="Select Device" /> */}
              </div>
            </div>
            <div className="panel-body">
              <button
                className="btn btn-primary"
                onClick={!ac.dc.loadingButton ? handleTopUsers : null}
                disabled={ac.dc.loadingButton}
              >
                {ac.dc.loadingButton && (
                  <i
                    className="fa fa-refresh fa-spin"
                    style={{ marginRight: "5px" }}
                  />
                )}
                {ac.dc.loadingButton && <span>Loading Data</span>}
                {!ac.dc.loadingButton && <span>RUN</span>}
              </button>
              {/* <button
                className="btn btn-primary"
                onClick={!ac.dc.loadingButton ? handleIndex : null}
                disabled={ac.dc.loadingButton}
              >
                {ac.dc.loadingButton && (
                  <i
                    className="fa fa-refresh fa-spin"
                    style={{ marginRight: "5px" }}
                  />
                )}
                {ac.dc.loadingButton && <span>index</span>}
                {!loadingButton && <span>RUN</span>}
              </button> */}

              <a
                href="#null"
                className="btn btn-primary"
                onClick={handleResults}
              >
                Show results
              </a>
            </div>
            {/* </div> */}
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            {showtable ? (
              <div>
                <div className="panel-body">
                  <MDBDataTableV5
                    hover
                    entriesOptions={[10]}
                    entries={10}
                    pagesAmount={10}
                    data={datatable_10}
                    pagingTop
                    searchTop
                    searchBottom={false}
                    exportToCSV={true}
                  />
                </div>
                <div className="panel-body">
                  <MDBDataTableV5
                    hover
                    entriesOptions={[10]}
                    entries={10}
                    pagesAmount={10}
                    data={datatable_30}
                    pagingTop
                    searchTop
                    searchBottom={false}
                    exportToCSV={true}
                  />
                </div>
                <div className="panel-body">
                  <MDBDataTableV5
                    hover
                    entriesOptions={[10]}
                    entries={10}
                    pagesAmount={10}
                    data={datatable_60}
                    pagingTop
                    searchTop
                    searchBottom={false}
                    exportToCSV={true}
                  />
                </div>
              </div>
            ) : (
              <div></div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
