import React, { useEffect, useState } from "react";
import { MDBDataTableV5 } from "mdbreact";

export default function NetworkTopUsers(ac) {
  const [showtable, setshowtable] = useState(false);
  const [trigger, settrigger] = useState(false);
  const [loading, setloading] = useState(false);
  const [showDescription, setshowDescription] = useState(false);
  const [alert, setalert] = useState(false);
  const [ROW1, setROW1] = useState([]);
  const [ROW2, setROW2] = useState([]);
  const [ROW3, setROW3] = useState([]);
  const [mapROW1, setmapROW1] = useState([]);
  const [mapROW2, setmapROW2] = useState([]);
  const [mapROW3, setmapROW3] = useState([]);

  const APIbody = {
    "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
    organizationId: `${ac.dc.organizationID}`,
    networkId: `${ac.dc.networkID}`,
    "X-CSRFToken": "frollo",
  };

  useEffect(() => {
    async function APIcall() {
      try {
        if (trigger === false) {
          return;
        }
        setloading(true);
        fetch("/index/", {
          method: ["POST"],
          cache: "no-cache",
          headers: {
            content_type: "application/json",
          },
          body: JSON.stringify(APIbody),
        }).then((response) => {
          return response.json;
        });
        fetch("/index/")
          .then((res) => res.json())
          .then((reports) => {
            ac.dc.setreports(reports.reports);
          })
          .then(() => setloading(false));
      } catch (err) {
        console.log("APIcall -> err", err);
        if (err) {
          setalert(true);
        }
      }
    }

    APIcall();
  }, [trigger]);

  const handleResults = (e) => {
    e.preventDefault();

    let R_obj1 = {};
    for (var x = 0; x < ac.dc.reports.length; x++) {
      R_obj1[x] = ac.dc.reports[x];
    }
    setROW1(R_obj1[0]);
    setROW2(R_obj1[1]);
    setROW3(R_obj1[2]);

    let R1 = [];
    ROW1.map((item) => {
      var rowModel = [
        {
          TotalusagekB: item[0],
          DownloadkB: item[1],
          UploadkB: item[2],
          Description: item[3],
          DHCPhostname: item[4],
          MACaddress: item[5],
          IPaddress: item[6],
          VLAN: item[7],
        },
      ];
      R1.push(...rowModel);
      setmapROW1(R1);
    });

    let R2 = [];
    ROW2.map((item) => {
      var rowModel = [
        {
          TotalusagekB: item[0],
          DownloadkB: item[1],
          UploadkB: item[2],
          Description: item[3],
          DHCPhostname: item[4],
          MACaddress: item[5],
          IPaddress: item[6],
          VLAN: item[7],
        },
      ];
      R2.push(...rowModel);
      setmapROW2(R2);
    });

    let R3 = [];
    ROW3.map((item) => {
      var rowModel = [
        {
          TotalusagekB: item[0],
          DownloadkB: item[1],
          UploadkB: item[2],
          Description: item[3],
          DHCPhostname: item[4],
          MACaddress: item[5],
          IPaddress: item[6],
          VLAN: item[7],
        },
      ];
      R3.push(...rowModel);
      setmapROW3(R3);
    });
    setshowtable(true);
  };

  const handleTopUsers = (e) => {
    e.preventDefault();
    settrigger(true);
  };

  const datatable_10 = {
    columns: [
      {
        label: "Total usage kB",
        field: "TotalusagekB",
        width: 150,
        attributes: {
          "aria-controls": "DataTable",
          "aria-label": "Description",
        },
      },
      {
        label: "Download kB",
        field: "DownloadkB",
        sort: "asc",
        width: 270,
      },
      {
        label: "Upload kB",
        field: "UploadkB",
        sort: "asc",
        width: 200,
      },
      {
        label: "Description",
        field: "Description",
        sort: "asc",
        width: 100,
      },
      {
        label: "DHCP hostname",
        field: "DHCPhostname",
        sort: "asc",
        width: 100,
      },
      {
        label: "MAC address",
        field: "MACaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "IP address",
        field: "IPaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "VLAN",
        field: "VLAN",
        sort: "asc",
        width: 100,
      },
    ],
    rows: mapROW1,
  };
  const datatable_30 = {
    columns: [
      {
        label: "Total usage kB",
        field: "TotalusagekB",
        width: 150,
        attributes: {
          "aria-controls": "DataTable",
          "aria-label": "Description",
        },
      },
      {
        label: "Download kB",
        field: "DownloadkB",
        sort: "asc",
        width: 270,
      },
      {
        label: "UploadkB",
        field: "VlanName",
        sort: "asc",
        width: 200,
      },
      {
        label: "Description",
        field: "Description",
        sort: "asc",
        width: 100,
      },
      {
        label: "DHCP hostname",
        field: "DHCPhostname",
        sort: "asc",
        width: 100,
      },
      {
        label: "MAC address",
        field: "MACaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "IP address",
        field: "IPaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "VLAN",
        field: "VLAN",
        sort: "asc",
        width: 100,
      },
    ],
    rows: mapROW2,
  };

  const datatable_60 = {
    columns: [
      {
        label: "Total usage kB",
        field: "TotalusagekB",
        width: 150,
        attributes: {
          "aria-controls": "DataTable",
          "aria-label": "Description",
        },
      },
      {
        label: "Download kB",
        field: "DownloadkB",
        sort: "asc",
        width: 270,
      },
      {
        label: "UploadkB",
        field: "VlanName",
        sort: "asc",
        width: 200,
      },
      {
        label: "Description",
        field: "Description",
        sort: "asc",
        width: 100,
      },
      {
        label: "DHCP hostname",
        field: "DHCPhostname",
        sort: "asc",
        width: 100,
      },
      {
        label: "MAC address",
        field: "MACaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "IP address",
        field: "IPaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "VLAN",
        field: "VLAN",
        sort: "asc",
        width: 100,
      },
    ],
    rows: mapROW3,
  };

  return (
    <div id="page-inner-main-templates">
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            <div className="panel-body">
              <div className="panel panel-success">
                <div className="custom-control custom-switch">
                  <input
                    checked={ac.dc.KmMilesToggle}
                    onChange={() => setshowDescription(!showDescription)}
                    type="checkbox"
                    className="custom-control-input"
                    id="customSwitch1"
                  />
                  <label
                    className="custom-control-label"
                    htmlFor="customSwitch1"
                  >
                    Hint
                  </label>
                </div>
                {/* <input
                  type="checkbox"
                  checked
                  data-toggle="toggle"
                  data-style="slow"
                  onChange={() => setshowDescription(!showDescription)}
                /> */}
                {showDescription ? (
                  <div>
                    <div className="panel-heading">Description</div>

                    <div className="panel-body">
                      <p>
                        This is script gets the top 10 heaviest bandwidth users
                        of an MX security appliance for the last 10, 30 and 60
                        minutes.
                      </p>
                    </div>
                  </div>
                ) : (
                  <div></div>
                )}

                <button
                  className="btn btn-primary"
                  onClick={!loading ? handleTopUsers : null}
                  disabled={loading}
                >
                  {loading && (
                    <i
                      className="fa fa-refresh fa-spin"
                      style={{ marginRight: "5px" }}
                    />
                  )}
                  {loading && <span>Loading Data</span>}
                  {!loading && <span>RUN</span>}
                </button>

                <a
                  href="#null"
                  className="btn btn-primary"
                  onClick={handleResults}
                >
                  Show results
                </a>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            {showtable ? (
              <div>
                <div className="panel-body">
                  <MDBDataTableV5
                    hover
                    entriesOptions={[10]}
                    entries={10}
                    pagesAmount={10}
                    data={datatable_10}
                    pagingTop
                    searchTop
                    searchBottom={false}
                    exportToCSV={true}
                  />
                </div>
                <div className="panel-body">
                  <MDBDataTableV5
                    hover
                    entriesOptions={[10]}
                    entries={10}
                    pagesAmount={10}
                    data={datatable_30}
                    pagingTop
                    searchTop
                    searchBottom={false}
                    exportToCSV={true}
                  />
                </div>
                <div className="panel-body">
                  <MDBDataTableV5
                    hover
                    entriesOptions={[10]}
                    entries={10}
                    pagesAmount={10}
                    data={datatable_60}
                    pagingTop
                    searchTop
                    searchBottom={false}
                    exportToCSV={true}
                  />
                </div>
              </div>
            ) : (
              <div></div>
            )}
          </div>
        </div>
      </div>
