import React, { useEffect, useState, useRef } from "react";
import "../../styles/FindPorts.css";

export default function NetworkTopUsers(ac) {
  const [showtable, setshowtable] = useState(false);
  const [trigger, settrigger] = useState(0);
  const [trigger2, settrigger2] = useState(0);
  const [findPort, setfindPort] = useState([]);
  const [errorMessage, seterrorMessage] = useState(null);
  // const [macAddress, setmacAddress] = useState("");
  const [macAddressbeforeTransf, setmacAddressbeforeTransf] = useState("");
  console.log(
    "NetworkTopUsers -> macAddressbeforeTransf",
    macAddressbeforeTransf
  );

  // console.log("NetworkTopUsers -> macAddress", macAddress);

  // const APIbody2 = {
  //   "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
  //   "X-CSRFToken": "frollo",
  //   ORG_ID: `${ac.dc.organizationID}`,
  //   MAC_ADDR: `${macAddress}`,
  // };

  const isFirstRun = useRef(true);
  useEffect(() => {
    if (isFirstRun.current) {
      isFirstRun.current = false;
      return;
    }
    async function APICallReports() {
      if (ac.dc.isOrgSelected && ac.dc.isNetSelected === true) {
        const transormMacAddress = (address) => {
          var a = address.replace(/\W/g, "");
          if (a.length === 12) {
            seterrorMessage(null);
            const macAddress = a
              .replace(/(.{2})/g, "$1:")
              .toUpperCase()
              .slice(0, -1);
          } else {
            seterrorMessage(
              <div className="form-input-error-msg alert alert-danger">
                <span className="glyphicon glyphicon-exclamation-sign"></span>
                Please insert a valid MAC Address. Format accepted are:
                "AA.BB.CC.DD.EE.FF", "112233445566", "AACC.DDEE.BBFF",
                "AA.BB.CC.DD.EE.FF", "AB12CD34EF56", "ab-12-cd-34-ef-56",
                "AB-12:CD-34:EF-56", "ab 12 cd 34 ef 56", "AB 12 CD 34 EF 56",
              </div>
            );
          }
        };

        [macAddressbeforeTransf].forEach(transormMacAddress);

        try {
          ac.dc.setloadingButton(true);

          fetch("/find_ports", {
            method: ["POST"],
            cache: "no-cache",
            headers: {
              content_type: "application/json",
            },
            body: JSON.stringify({
              "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
              "X-CSRFToken": "frollo",
              ORG_ID: `${ac.dc.organizationID}`,
              MAC_ADDR: "A4:5D:36:63:14:CE",
            }),
          })
            .then((response) => response.json())
            .then((data) => setfindPort(data.data))

            .then(() => ac.dc.setloadingButton(false))
            .then(() => setshowtable(true));
        } catch (err) {
          if (err) {
            console.log(err);
          }
        }
      } else {
        ac.dc.setswitchAlertModal(true);
        ac.dc.setswitchToolsTemplate(false);
      }
    }
    APICallReports();
    return () => {
      setfindPort([]);
      setshowtable(false);
    };
    // eslint-disable-next-line
  }, [trigger]);

  const handleTopUsers = (e) => {
    e.preventDefault();
    settrigger(trigger + 1);
    settrigger2(trigger2 + 1);
    // alert(macAddress);
  };

  // VALIDATION FORM

  // const firstRender = useRef(true);

  // useEffect(() => {
  //   if (firstRender.current) {
  //     firstRender.current = false;
  //     return;
  //   }
  //   const transormMacAddress = (address) => {
  //     var a = address.replace(/\W/g, "");
  //     if (a.length === 12) {
  //       seterrorMessage(null);
  //       setmacAddress(
  //         a
  //           .replace(/(.{2})/g, "$1:")
  //           .toUpperCase()
  //           .slice(0, -1)
  //       );
  //     } else {
  //       seterrorMessage(
  //         <div className="form-input-error-msg alert alert-danger">
  //           <span className="glyphicon glyphicon-exclamation-sign"></span>
  //           Please insert a valid MAC Address. Format accepted are:
  //           "AA.BB.CC.DD.EE.FF", "112233445566", "AACC.DDEE.BBFF",
  //           "AA.BB.CC.DD.EE.FF", "AB12CD34EF56", "ab-12-cd-34-ef-56",
  //           "AB-12:CD-34:EF-56", "ab 12 cd 34 ef 56", "AB 12 CD 34 EF 56",
  //         </div>
  //       );
  //     }
  //   };

  //   [macAddressbeforeTransf].forEach(transormMacAddress);
  // }, [trigger2]);

  return (
    <div id="page-inner-main-templates">
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            <div className="panel-body">
              <div className="panel-group" id="accordion">
                <div className="panel panel-default">
                  <div className="panel-heading">
                    <h4 className="panel-title-description">
                      <a
                        data-toggle="collapse"
                        data-parent="#accordion"
                        href="#collapseOne"
                        className="collapsed"
                      >
                        Description
                      </a>
                    </h4>
                  </div>
                  <div id="collapseOne" className="panel-collapse collapse">
                    <div className="panel-body">
                      This script finds all MS switchports that match the input
                      search parameter, searching either by clients from a file
                      listing MAC addresses (one per line), a specific tag in
                      Dashboard currently applied to ports, or the specific
                      access policy currently configured.
                    </div>
                  </div>
                </div>
              </div>
              <div className="d-flex"></div>
            </div>
            <div className="panel-body">
              <div className="form-group">
                <form
                  className="form-inline"
                  name="contentForm"
                  method=""
                  action=""
                >
                  <input
                    type="text"
                    className="form-control"
                    placeholder="MAC Address"
                    onChange={(e) => setmacAddressbeforeTransf(e.target.value)}
                    name="mac-address"
                    id="mac-address"
                    required="required"
                    data-error="Please enter a valid mac-address."
                  />

                  {errorMessage && <span>{errorMessage}</span>}
                </form>
              </div>
              <button
                id="runButton"
                type="submit"
                className="btn btn-primary"
                onClick={!ac.dc.loadingButton ? handleTopUsers : null}
                disabled={ac.dc.loadingButton}
              >
                {ac.dc.loadingButton && (
                  <i
                    className="fa fa-refresh fa-spin"
                    style={{ marginRight: "5px" }}
                  />
                )}
                {ac.dc.loadingButton && <span>Loading Data</span>}
                {!ac.dc.loadingButton && <span>RUN</span>}
              </button>
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            {showtable ? (
              <div className="panel-body">
                <div className="table-responsive table-bordered">
                  <table className="table">
                    <thead>
                      <tr>
                        <th>#</th>
                        <th>Switch</th>
                        <th>Port</th>
                        <th>IP Address</th>
                        <th>MAC Address</th>
                      </tr>
                    </thead>
                    <tbody>
                      <tr>
                        <td>1</td>
                        <td>{findPort[0]}</td>
                        <td>{findPort[1]}</td>
                        <td></td>
                        <td></td>
                      </tr>
                    </tbody>
                  </table>
                </div>
              </div>
            ) : (
              <div></div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
