import React, { useState, useEffect } from "react";
import Template from "./components/Template";
import LoginAPI from "./components/LoginAPI";
import Dashboard from "./components/Dashboard";

const MainContext = React.createContext(null);

function App() {
  const InitialKey = "12345";
  const [apiKey, setapiKey] = useState({
    "X-Cisco-Meraki-API-Key": `${InitialKey}`,
  });
  const [organization, setorganization] = useState("Meraki");
  const [organizationID, setorganizationID] = useState(0);
  const [timeZone, settimeZone] = useState(0);
  const [network, setnetwork] = useState(0);
  const [loginName, setloginName] = useState("");
  const [loginEmail, setloginEmail] = useState("");
  const [inputKey, setinputKey] = useState();
  const [inputConfKey, setinputConfKey] = useState();
  const [isLoggedIn, setisLoggedIn] = useState(false);
  const [switchLoginAPI, setswitchLoginAPI] = useState(false);
  const [switchDashboard, setswitchDashboard] = useState(true);
  const [classLogin, setclassLogin] = useState("");
  const [classDashboard, setclassDashboard] = useState("active-menu");

  console.log(apiKey);

  useEffect(() => {
    const data = localStorage.getItem("my-token");
    if (data) {
      setapiKey(JSON.parse(data));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("my-token", JSON.stringify(apiKey));
  });

  useEffect(() => {
    fetch("/organizations", {
      method: ["POST"],
      cache: "no-cache",
      headers: {
        content_type: "application/json",
      },
      body: JSON.stringify(apiKey),
    }).then((response) => {
      return response.json;
    });
    fetch("/organizations")
      .then((res) => res.json())
      .then((organizations) => {
        setorganization(organizations.organizations[0].name);
        setorganizationID(organizations.organizations[0].id);
      });
  }, [apiKey]);

  useEffect(() => {
    fetch("/networks", {
      method: ["POST"],
      cache: "no-cache",
      headers: {
        content_type: "application/json",
      },
      body: JSON.stringify(apiKey),
    }).then((response) => {
      return response.json;
    });
    fetch("/networks")
      .then((res) => res.json())
      .then((network) => {
        setnetwork(network.networks[0].name);
        settimeZone(network.networks[0].timeZone);
      });
  }, [apiKey]);

  const dc = {
    organization,
    setorganization,
    organizationID,
    setorganizationID,
    timeZone,
    settimeZone,
    network,
    setnetwork,
    apiKey,
    setapiKey,
    loginName,
    setloginName,
    loginEmail,
    setloginEmail,
    inputKey,
    setinputKey,
    inputConfKey,
    setinputConfKey,
    isLoggedIn,
    setisLoggedIn,
    switchLoginAPI,
    setswitchLoginAPI,
    switchDashboard,
    setswitchDashboard,
    classLogin,
    setclassLogin,
    classDashboard,
    setclassDashboard,
  };

  return (
    <MainContext.Provider dc={dc}>
      <Template dc={dc} />
    </MainContext.Provider>
  );
}

export default App;
