import React, { useEffect, useState, useRef } from "react";
import "../../styles/FindPorts.css";

export default function NetworkTopUsers(ac) {
  const [showtable, setshowtable] = useState(false);
  const [trigger, settrigger] = useState(0);
  const [trigger2, settrigger2] = useState(0);
  const [findPort, setfindPort] = useState([]);
  const [macAddress, setmacAddress] = useState("");
  const [errorMessage, seterrorMessage] = useState(null);

  console.log("NetworkTopUsers -> macAddress", macAddress);

  const APIbody2 = {
    "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
    "X-CSRFToken": "frollo",
    ORG_ID: `${ac.dc.organizationID}`,
    MAC_ADDR: `${macAddress}`,
  };

  const isFirstRun = useRef(true);
  useEffect(() => {
    if (isFirstRun.current) {
      isFirstRun.current = false;
      return;
    }
    async function APICallReports() {
      if (ac.dc.isOrgSelected && ac.dc.isNetSelected === true) {
        try {
          ac.dc.setloadingButton(true);

          fetch("/find_ports", {
            method: ["POST"],
            cache: "no-cache",
            headers: {
              content_type: "application/json",
            },
            body: JSON.stringify(APIbody2),
          })
            .then((response) => response.json())
            .then((data) => setfindPort(data.data))

            .then(() => ac.dc.setloadingButton(false))
            .then(() => setshowtable(true));
        } catch (err) {
          if (err) {
            console.log(err);
          }
        }
      } else {
        ac.dc.setswitchAlertModal(true);
        ac.dc.setswitchToolsTemplate(false);
      }
    }
    APICallReports();
    return () => {
      setfindPort([]);
      setshowtable(false);
    };
    // eslint-disable-next-line
  }, [trigger]);

  const handleTopUsers = (e) => {
    e.preventDefault();
    settrigger(trigger + 1);
    setshowtable(true);
  };

  const triggermactransf = () => {
    settrigger2(trigger2 + 1);
  };

  useEffect(() => {
    const transormMacAddress = (address) => {
      var a = address.replace(/\W/g, "");
      if (a.length === 12) {
        console.log(
          a
            .replace(/(.{2})/g, "$1:")
            .toUpperCase()
            .slice(0, -1)
        );
      } else {
        seterrorMessage(
          <div class="form-input-error-msg alert alert-danger">
            <span class="glyphicon glyphicon-exclamation-sign"></span>
            Please insert a valid MAC Address
          </div>
        );
      }
    };

    [macAddress].forEach(transormMacAddress);
  }, [macAddress, trigger2]);

  // VALIDATION FORM
  // const firstRender = useRef(true);

  // useEffect(() => {
  //   // we want to skip validation on first render
  //   if (firstRender.current) {
  //     firstRender.current = false;
  //     return;
  //   }

  //   formValidation();
  // }, [macAddress]); // any state variable(s) included in here will trigger the effect to run

  // const formValidation = () => {
  //   if (macAddress === "") {
  //     seterrorMessage(
  //       <div class="form-input-error-msg alert alert-danger">
  //         <span class="glyphicon glyphicon-exclamation-sign"></span>
  //         Please insert a valid MAC Address
  //       </div>
  //     );
  //     return true;
  //   } else {
  //     seterrorMessage(null);
  //     return false;
  //   }
  // };

  const handleSave = () => {
    // ...
  };

  return (
    <div id="page-inner-main-templates">
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            <div className="panel-body">
              <div className="panel-group" id="accordion">
                <div className="panel panel-default">
                  <div className="panel-heading">
                    <h4 className="panel-title-description">
                      <a
                        data-toggle="collapse"
                        data-parent="#accordion"
                        href="#collapseOne"
                        className="collapsed"
                      >
                        Description
                      </a>
                    </h4>
                  </div>
                  <div id="collapseOne" className="panel-collapse collapse">
                    <div className="panel-body">
                      This script finds all MS switchports that match the input
                      search parameter, searching either by clients from a file
                      listing MAC addresses (one per line), a specific tag in
                      Dashboard currently applied to ports, or the specific
                      access policy currently configured.
                    </div>
                  </div>
                </div>
              </div>
              <div className="d-flex"></div>
            </div>
            <div className="panel-body">
              <div className="form-group">
                <form onSubmit={handleSave}>
                  <input
                    type="text"
                    className="form-control"
                    placeholder="MAC Address"
                    onChange={(e) => setmacAddress(e.target.value)}
                    name="mac-address"
                    id="mac-address"
                    required="required"
                    data-error="Please enter a valid mac-address."
                  />

                  {errorMessage && <p>{errorMessage}</p>}
                  {/* <button
                    type="submit"
                    disabled={disable}
                    class="btn btn-primary btn-lg"
                  >
                    Save
                  </button> */}
                </form>
              </div>

              {/* <form >
                <div class="form-group">
                  <label class="control-label" for="mac-address">
                    Text 1
                  </label>
                  <div class="form-input-error-msg alert alert-danger">
                    <span class="glyphicon glyphicon-exclamation-sign"></span>
                    This field is mandatary
                  </div>
                  <input
                    type="text"
                    id="mac-address"
                    class="form-control validate"
                    onChange={(e) => setmacAddress(e.target.value)}
                  />
                </div>

                <button id="submit" class="btn btn-primary btn-lg">
                  Sumbit
                </button>
              </form> */}
              <button
                id="runButton"
                type="submit"
                className="btn btn-primary"
                onClick={!ac.dc.loadingButton ? triggermactransf : null}
                disabled={ac.dc.loadingButton}
              >
                {ac.dc.loadingButton && (
                  <i
                    className="fa fa-refresh fa-spin"
                    style={{ marginRight: "5px" }}
                  />
                )}
                {ac.dc.loadingButton && <span>Loading Data</span>}
                {!ac.dc.loadingButton && <span>RUN</span>}
              </button>
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            {showtable ? (
              <div className="panel-body">
                <div className="table-responsive table-bordered">
                  <table className="table">
                    <thead>
                      <tr>
                        <th>#</th>
                        <th>Switch</th>
                        <th>Port</th>
                        <th>IP Address</th>
                        <th>MAC Address</th>
                      </tr>
                    </thead>
                    <tbody>
                      <tr>
                        <td>1</td>
                        <td>{findPort[0]}</td>
                        <td>{findPort[1]}</td>
                        <td></td>
                        <td></td>
                      </tr>
                    </tbody>
                  </table>
                </div>
              </div>
            ) : (
              <div></div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
