  //       ac.dc.setreports(reports.reports);
  //     });
  // }, [trigger]);

  useEffect(() => {
    async function APICallReports() {
      try {
        if (trigger === false) {
          return;
        }
        setloading(true);

        fetch("/get_report/")
          .then((res) => res.json())
          .then((reports) => {
            ac.dc.setreports(reports.reports);
          })
          .then(() => setloading(false));
      } catch (err) {
        if (err) {
          setalert(true);
        }
      }
    }

    APICallReports();
  }, [trigger]);

  // const APIbody2 = {
  //   "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
  //   ORG_ID: `${ac.dc.organizationID}`,
  //   ORG_LIST: ac.dc.organizationList,
  //   NETWORK_ID: `${ac.dc.networkID}`,
  //   "X-CSRFToken": "frollo",
  //   ARG_ORGNAME: `${ac.dc.organization}`,
  //   NETWORK_NAME: `${ac.dc.network}`,
  //   NETWORK_LIST: ac.dc.networkList,
  // };

  const ORGANIZATIONS = ac.dc.organizationList.map((opt, index) => ({
    label: opt.name,
    value: index,
    id: opt.id,
  }));

  const NETWORKS = ac.dc.networkList.map((opt, index) => ({
    label: opt.name,
    value: index,
    id: opt.id,
  }));

  const DEV = Object.values(ac.dc.reports);
  const DEVICES = DEV[3].map((opt, index) => ({
    label: opt[0],
    value: index,
  }));

  const HandleOrganization = (opt) => {
    ac.dc.setorganization(opt.label);
    ac.dc.setorganizationID(opt.id);
    ac.dc.setnetwork("Networks");
  };
  const HandleNetwork = (opt) => {
    ac.dc.setnetwork(opt.label);
    ac.dc.setnetworkID(opt.id);
  };

  // const APIbody = {
  //   "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
  //   ORG_ID: `${ac.dc.organizationID}`,
  //   NETWORK_ID: `${ac.dc.networkID}`,
  //   "X-CSRFToken": "frollo",
  //   ARG_ORGNAME: `${ac.dc.organization}`,
  //   NETWORK_NAME: `${ac.dc.network}`,
  //   NETWORK_LIST: `${ac.dc.networkList}`,
  // };

