      R_obj1[x] = ac.dc.reports[x];
    }
    setROW1(R_obj1[0]);
    setROW2(R_obj1[1]);
    setROW3(R_obj1[2]);

    let R1 = [];
    ROW1.map((item) => {
      var rowModel = [
        {
          TotalusagekB: item[0],
          DownloadkB: item[1],
          UploadkB: item[2],
          Description: item[3],
          DHCPhostname: item[4],
          MACaddress: item[5],
          IPaddress: item[6],
          VLAN: item[7],
        },
      ];
      R1.push(...rowModel);
      setmapROW1(R1);
    });

