      var rowModel = [
        {
          TotalusagekB: item[0],
          DownloadkB: item[1],
          UploadkB: item[2],
          Description: item[3],
          DHCPhostname: item[4],
          MACaddress: item[5],
          IPaddress: item[6],
          VLAN: item[7],
