      {
        label: "Download kB",
        field: "DownloadkB",
        sort: "asc",
        width: 270,
      },
      {
        label: "Upload kB",
        field: "UploadkB",
        sort: "asc",
        width: 200,
      },
      {
        label: "Description",
        field: "Description",
        sort: "asc",
        width: 100,
      },
      {
        label: "DHCP hostname",
        field: "DHCPhostname",
        sort: "asc",
        width: 100,
      },
      {
        label: "MAC address",
        field: "MACaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "IP address",
        field: "IPaddress",
        sort: "asc",
