  const [mapROW1, setmapROW1] = useState([]);
  const [mapROW2, setmapROW2] = useState([]);
  const [mapROW3, setmapROW3] = useState([]);

  const ORGANIZATION = ac.dc.organizationList.map((opt, index) => ({
    label: opt.name,
    value: index,
  }));

  const scaryAnimals = [
    { label: "Alligators", value: 1 },
    { label: "Crocodiles", value: 2 },
    { label: "Sharks", value: 3 },
    { label: "Small crocodiles", value: 4 },
    { label: "Smallest crocodiles", value: 5 },
    { label: "Snakes", value: 6 },
  ];

  const APIbody = {
    "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
    ORG_ID: `${ac.dc.organizationID}`,
    NETWORK_ID: `${ac.dc.networkID}`,
    "X-CSRFToken": "frollo",
    ARG_ORGNAME: `${ac.dc.organization}`,
    NETWORK_NAME: `${ac.dc.network}`,
    NETWORK_LIST: `${ac.dc.networkList}`,
  };

  useEffect(() => {
    async function APIcall() {
      try {
        if (trigger === false) {
          return;
        }
        setloading(true);
        fetch("/topuserdata/", {
          method: ["POST"],
          cache: "no-cache",
          headers: {
            content_type: "application/json",
          },
          body: JSON.stringify(APIbody),
        })
          .then((response) => {
            return response.json;
          })
          .then(() => setloading(false));
      } catch (err) {
        if (err) {
          setalert(true);
        }
      }
    }

    APIcall();
  }, [trigger]);

  useEffect(() => {
    async function APIcall2() {
      try {
        if (trigger === false) {
          return;
        }
        setloading(true);

        fetch("/index/")
          .then((res) => res.json())
          .then((reports) => {
            ac.dc.setreports(reports.reports);
            console.log("NetworkTopUsers -> reports", reports);
          })
          .then(() => setloading(false));
      } catch (err) {
        if (err) {
          setalert(true);
        }
      }
    }

    APIcall2();
  }, [trigger2]);

  const handleResults = (e) => {
    e.preventDefault();

    let R_obj1 = {};
    for (var x = 0; x < ac.dc.reports.length; x++) {
      R_obj1[x] = ac.dc.reports[x];
    }
    setROW1(R_obj1[0]);
    setROW2(R_obj1[1]);
    setROW3(R_obj1[2]);

    let R1 = [];
    ROW1.map((item) => {
      var rowModel = [
        {
          TotalusagekB: item[0],
          DownloadkB: item[1],
          UploadkB: item[2],
          Description: item[3],
          DHCPhostname: item[4],
          MACaddress: item[5],
          IPaddress: item[6],
          VLAN: item[7],
        },
      ];
      R1.push(...rowModel);
      setmapROW1(R1);
    });

    let R2 = [];
    ROW2.map((item) => {
      var rowModel = [
        {
          TotalusagekB: item[0],
          DownloadkB: item[1],
          UploadkB: item[2],
          Description: item[3],
          DHCPhostname: item[4],
          MACaddress: item[5],
          IPaddress: item[6],
          VLAN: item[7],
        },
      ];
      R2.push(...rowModel);
      setmapROW2(R2);
    });

    let R3 = [];
    ROW3.map((item) => {
      var rowModel = [
        {
          TotalusagekB: item[0],
          DownloadkB: item[1],
          UploadkB: item[2],
          Description: item[3],
          DHCPhostname: item[4],
          MACaddress: item[5],
          IPaddress: item[6],
          VLAN: item[7],
        },
      ];
      R3.push(...rowModel);
      setmapROW3(R3);
    });
    setshowtable(true);
  };

  const handleTopUsers = (e) => {
    e.preventDefault();
    settrigger(true);
  };
  const handleIndex = (e) => {
    e.preventDefault();
    settrigger2(true);
  };

  const datatable_10 = {
    columns: [
      {
        label: "Total usage kB",
        field: "TotalusagekB",
        width: 150,
        attributes: {
          "aria-controls": "DataTable",
          "aria-label": "Description",
        },
      },
      {
        label: "Download kB",
        field: "DownloadkB",
        sort: "asc",
        width: 270,
      },
      {
        label: "Upload kB",
        field: "UploadkB",
        sort: "asc",
        width: 200,
      },
      {
        label: "Description",
        field: "Description",
        sort: "asc",
        width: 100,
      },
      {
        label: "DHCP hostname",
        field: "DHCPhostname",
        sort: "asc",
        width: 100,
      },
      {
        label: "MAC address",
        field: "MACaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "IP address",
        field: "IPaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "VLAN",
        field: "VLAN",
        sort: "asc",
        width: 100,
      },
    ],
    rows: mapROW1,
  };
  const datatable_30 = {
    columns: [
      {
        label: "Total usage kB",
        field: "TotalusagekB",
        width: 150,
        attributes: {
          "aria-controls": "DataTable",
          "aria-label": "Description",
        },
      },
      {
        label: "Download kB",
        field: "DownloadkB",
        sort: "asc",
        width: 270,
      },
      {
        label: "UploadkB",
        field: "VlanName",
        sort: "asc",
        width: 200,
      },
      {
        label: "Description",
        field: "Description",
        sort: "asc",
        width: 100,
      },
      {
        label: "DHCP hostname",
        field: "DHCPhostname",
        sort: "asc",
        width: 100,
      },
      {
        label: "MAC address",
        field: "MACaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "IP address",
        field: "IPaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "VLAN",
        field: "VLAN",
        sort: "asc",
        width: 100,
      },
    ],
    rows: mapROW2,
  };

  const datatable_60 = {
    columns: [
      {
        label: "Total usage kB",
        field: "TotalusagekB",
        width: 150,
        attributes: {
          "aria-controls": "DataTable",
          "aria-label": "Description",
        },
      },
      {
        label: "Download kB",
        field: "DownloadkB",
        sort: "asc",
        width: 270,
      },
      {
        label: "UploadkB",
        field: "VlanName",
        sort: "asc",
        width: 200,
      },
      {
        label: "Description",
        field: "Description",
        sort: "asc",
        width: 100,
      },
      {
        label: "DHCP hostname",
        field: "DHCPhostname",
        sort: "asc",
        width: 100,
      },
      {
        label: "MAC address",
        field: "MACaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "IP address",
        field: "IPaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "VLAN",
        field: "VLAN",
        sort: "asc",
        width: 100,
      },
    ],
    rows: mapROW3,
  };

  return (
    <div id="page-inner-main-templates">
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            <div className="panel-body">
              <div class="panel-group" id="accordion">
                <div class="panel panel-default">
                  <div class="panel-heading">
                    <h4 class="panel-title-description">
                      <a
                        data-toggle="collapse"
                        data-parent="#accordion"
                        href="#collapseOne"
                        class="collapsed"
                      >
                        Description
                      </a>
                    </h4>
                  </div>
                  <div id="collapseOne" class="panel-collapse collapse">
                    <div class="panel-body">
                      This is script gets the top 10 heaviest bandwidth users of
                      an MX security appliance for the last 10, 30 and 60
                      minutes.
                    </div>
                  </div>
                </div>
              </div>
              <div className="d-flex">
                <Select className="p-2-tools_template" options={ORGANIZATION} />
              </div>
              <div class="d-flex">
                <div class="p-2-tools_template">
                  <select
                    // onChange={shutAllTools}
                    id="selectBox"
                    className="btn btn-default dropdown-toggle-tools"
                  >
                    <optgroup label="Tshoot Tools">
                      <option className="option-tools-disabled" value="0">
                        Set Organization
                      </option>
                      <option value="1">Get all devices IPs</option>
                    </optgroup>
                  </select>
                </div>
                <div class="p-2-tools_template">
                  <select
                    // onChange={shutAllTools}
                    id="selectBox"
                    className="btn btn-default dropdown-toggle-tools"
                  >
                    <optgroup label="Tshoot Tools">
                      <option className="option-tools-disabled" value="0">
                        Set Organization
                      </option>
                      <option value="1">Get all devices IPs</option>
                    </optgroup>
                  </select>
                </div>

                <div class="p-2-tools_template ">
                  <select
                    // onChange={shutAllTools}
                    id="selectBox"
                    className="btn btn-default dropdown-toggle-tools"
                  >
                    <optgroup label="Tshoot Tools">
                      <option className="option-tools-disabled" value="0">
                        Networks
                      </option>
                      <option value="1">Get all devices IPs</option>
                      <option value="2">Get all subnets</option>
                      <option value="3">Get all Networks subnets</option>
                      <option value="4">Network Top Users Report</option>
                    </optgroup>
                  </select>
                </div>
                <div class="p-2-tools_template">
                  <select
                    // onChange={shutAllTools}
                    id="selectBox"
                    className="btn btn-default dropdown-toggle-tools"
                  >
                    <optgroup label="Tshoot Tools">
                      <option className="option-tools-disabled" value="0">
                        Select Device
                      </option>
                      <option value="1">Get all devices IPs</option>
                      <option value="2">Get all subnets</option>
                      <option value="3">Get all Networks subnets</option>
                      <option value="4">Network Top Users Report</option>
                    </optgroup>
                  </select>
                </div>
              </div>
            </div>
            <div className="panel-body">
              <button
                className="btn btn-primary"
                onClick={!loading ? handleTopUsers : null}
                disabled={loading}
              >
                {loading && (
                  <i
                    className="fa fa-refresh fa-spin"
                    style={{ marginRight: "5px" }}
                  />
                )}
                {loading && <span>topusers</span>}
                {!loading && <span>RUN</span>}
              </button>
              <button
                className="btn btn-primary"
                onClick={!loading ? handleIndex : null}
                disabled={loading}
              >
                {loading && (
                  <i
                    className="fa fa-refresh fa-spin"
                    style={{ marginRight: "5px" }}
                  />
                )}
                {loading && <span>index</span>}
                {!loading && <span>RUN</span>}
              </button>

              <a
                href="#null"
                className="btn btn-primary"
                onClick={handleResults}
              >
                Show results
              </a>
            </div>
            {/* </div> */}
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            {showtable ? (
              <div>
                <div className="panel-body">
                  <MDBDataTableV5
                    hover
                    entriesOptions={[10]}
                    entries={10}
                    pagesAmount={10}
                    data={datatable_10}
                    pagingTop
                    searchTop
                    searchBottom={false}
                    exportToCSV={true}
                  />
                </div>
                <div className="panel-body">
                  <MDBDataTableV5
                    hover
                    entriesOptions={[10]}
                    entries={10}
                    pagesAmount={10}
                    data={datatable_30}
                    pagingTop
                    searchTop
                    searchBottom={false}
                    exportToCSV={true}
                  />
                </div>
                <div className="panel-body">
                  <MDBDataTableV5
                    hover
                    entriesOptions={[10]}
                    entries={10}
                    pagesAmount={10}
                    data={datatable_60}
                    pagingTop
                    searchTop
                    searchBottom={false}
                    exportToCSV={true}
                  />
                </div>
              </div>
            ) : (
              <div></div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
