import React, { useEffect, useState } from "react";
import { MDBDataTableV5 } from "mdbreact";
// import "@fortawesome/fontawesome-free/css/all.min.css";
// import "bootstrap-css-only/css/bootstrap.min.css";
// import "mdbreact/dist/css/mdb.css";

export default function GetAllDevicesIP(ac) {
  const APIbody = {
    "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
    organizationId: `${ac.dc.organizationID}`,
    networkId: `${ac.dc.networkID}`,
  };

  useEffect(() => {
    fetch("/clients", {
      method: ["POST"],
      cache: "no-cache",
      headers: {
        content_type: "application/json",
      },
      body: JSON.stringify(APIbody),
    }).then((response) => {
      return response.json;
    });
    fetch("/devices")
      .then((res) => res.json())
      .then((client) => {
        ac.dc.setclientList(client.devices);
      });
  }, [ac.dc.networkID]);

  const datatable = {
    columns: [
      {
        label: "Description",
        field: "Description",
        width: 150,
        attributes: {
          "aria-controls": "DataTable",
          "aria-label": "Description",
        },
      },
      {
        label: "Model",
        field: "Model",
        sort: "asc",
        width: 270,
      },
      {
        label: "LAN IP address",
        field: "LAN_IP_address",
        sort: "asc",
        width: 200,
      },
      {
        label: "MAC address",
        field: "MAC_address",
        sort: "asc",
        width: 100,
      },
      {
        label: "WAN 1 IP",
        field: "WAN_1_IP",
        sort: "asc",
        width: 150,
      },
      {
        label: "WAN 2 IP",
        field: "WAN 2 IP",
        sort: "asc",
        width: 100,
      },
      {
        label: "Serial",
        field: "Serial",
        sort: "asc",
        width: 100,
      },
    ],
    rows: ac.dc.mapRows,
  };

  return (
    <div>
      <MDBDataTableV5
        hover
        entriesOptions={[5, 20, 25]}
        entries={5}
        pagesAmount={4}
        data={datatable}
        pagingTop
        searchTop
        searchBottom={false}
        exportToCSV={true}
      />
    </div>
  );
}
