                  UploadkB: item[2],
                  Description: item[3],
                  DHCPhostname: item[4],
                  MACaddress: item[5],
                  IPaddress: item[6],
                  VLAN: item[7],
                },
              ];
              R2.push(...rowModel);
              setmapROW2(R2);
            });

            let R3 = [];
            ROW3.map((item) => {
              var rowModel = [
                {
                  TotalusagekB: item[0],
                  DownloadkB: item[1],
                  UploadkB: item[2],
                  Description: item[3],
                  DHCPhostname: item[4],
                  MACaddress: item[5],
                  IPaddress: item[6],
                  VLAN: item[7],
                },
              ];
              R3.push(...rowModel);
              setmapROW3(R3);
            });
          })

          .then(() => ac.dc.setloadingButton(false));
        setTimeout(() => {
          setshowtable(true);
        }, [4000]);
        // .then(() => setshowtable(true));
