  const [mapROW2, setmapROW2] = useState([]);
  const [mapROW3, setmapROW3] = useState([]);

  console.log("Topusers -> deviceList", ac.dc.deviceList);

  const APIbody2 = {
    "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
    "X-CSRFToken": "frollo",
    ARG_ORGNAME: `${ac.dc.organization}`,
    SERIAL_NUM: `${ac.dc.SNtopUsers}`,
    NET_ID: `${ac.dc.networkID}`,
  };

  useEffect(() => {
    try {
      if (trigger === false) {
        return;
      }
      ac.dc.setloadingButton(true);
      fetch("/get_report/");
      fetch("/topuserdata/", {
        method: ["POST"],
        cache: "no-cache",
        headers: {
          content_type: "application/json",
        },
        body: JSON.stringify(APIbody2),
      })
        .then((response) => {
          return response.json;
        })

        .then((res) => res.json())
        .then((reports) => {
          ac.dc.setreports(reports.reports);
        })
        .then(() => ac.dc.setloadingButton(false));
    } catch (err) {
      if (err) {
        console.log(err);
      }
    }
  }, [trigger]);

  // useEffect(() => {
  //   async function APICallReports() {
  //     try {
  //       if (trigger === false) {
  //         return;
  //       }
  //       ac.dc.setloadingButton(true);

