import React, { useEffect, useState } from "react";
import { MDBDataTableV5 } from "mdbreact";

export default function NetworkTopUsers(ac) {
  const [showtable, setshowtable] = useState(false);
  const [trigger, settrigger] = useState(false);
  const [loading, setloading] = useState(false);
  const [alert, setalert] = useState(false);
  const APIbody = {
    "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
    organizationId: `${ac.dc.organizationID}`,
    networkId: `${ac.dc.networkID}`,
    "X-CSRFToken": "frollo",
  };

  useEffect(() => {
    async function APIcall() {
      try {
        if (trigger === false) {
          return;
        }
        setloading(true);
        fetch("/index/", {
          method: ["POST"],
          cache: "no-cache",
          headers: {
            content_type: "application/json",
          },
          body: JSON.stringify(APIbody),
        }).then((response) => {
          return response.json;
        });
        fetch("/index/")
          .then((res) => res.json())
          .then((reports) => {
            ac.dc.setreports(reports.reports);
          })
          .then(() => setloading(false));
      } catch (err) {
        console.log("APIcall -> err", err);
        if (err) {
          setalert(true);
        }
      }
    }

    APIcall();
  }, [trigger]);

  const handleResults = (e) => {
    e.preventDefault();

    let reportObject = {};
    for (var x = 0; x < ac.dc.reports.length; x++) {
      reportObject[x] = ac.dc.reports[x];
    }
    const REPORT = Object.values(reportObject);
    console.log("handleResults -> REPORT", REPORT);
    console.log("handleResults -> reports", ac.dc.reports);

