import React, { useEffect, useState, useRef } from "react";
import { MDBDataTableV5 } from "mdbreact";

export default function GetAllOrganizationSubnets(ac) {
  const [showtable, setshowtable] = useState(false);
  const [trigger, settrigger] = useState(0);
  const [loading, setloading] = useState(false);
  const [mapRows, setmapRows] = useState([]);

  // eslint-disable-next-line
  const [alert, setalert] = useState(false);

  const APIbody = {
    "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
    organizationId: `${ac.dc.organizationID}`,
    networkId: `${ac.dc.networkID}`,
  };

  const handleOrganizationSubnets = (e) => {
    e.preventDefault();
    settrigger(trigger + 1);
  };

  const isFirstRun = useRef(true);
  useEffect(() => {
    if (isFirstRun.current) {
      isFirstRun.current = false;
      return;
    }
    async function APIcall() {
      if (ac.dc.isOrgSelected && ac.dc.isNetSelected === true) {
        try {
          setloading(true);
          fetch("/allVlans", {
            method: ["POST"],
            cache: "no-cache",
            headers: {
              content_type: "application/json",
            },
            body: JSON.stringify(APIbody),
          }).then((response) => {
            return response.json;
          });

          fetch("/allVlans")
            .then((res) => res.json())
            .then((allVlans) => {
              ac.dc.setallVlanList(allVlans.result);

              let Vlanobjects = {};
              let Nameobjects = {};
              for (var x = 0; x < allVlans.result.length; x++) {
                Vlanobjects[x] = allVlans.result[x].allVlans;
                Nameobjects[x] = allVlans.result[x].networkname;
              }
              const VLANS = Object.values(Vlanobjects);

              let row = [];
              // eslint-disable-next-line
              VLANS.map((item) => {
                row.push(...item);
              });

              let row2 = [];
              // eslint-disable-next-line
              row.map((item) => {
                var rowModel = [
                  {
                    Subnet: item.subnet,
                    VlanID: item.id,
                    VlanName: item.name,
                    MX_IP: item.applianceIp,
                    DNS: item.dnsNameservers,
                    Network: "Network ---",
                  },
                ];
                row2.push(...rowModel);
                setmapRows(row2);
              });

              setshowtable(true);
            })

            .then(() => setloading(false));
        } catch (err) {
          if (err) {
            ac.dc.setalert(true);
          }
        }
      } else {
        ac.dc.setswitchAlertModal(true);
        ac.dc.setswitchToolsTemplate(false);
      }
    }
    APIcall();
    return () => {
      ac.dc.setallVlanList([]);
      setmapRows([]);
      setshowtable(false);
    };
    // eslint-disable-next-line
  }, [trigger]);

  const datatable = {
    columns: [
      {
        label: "Subnet",
        field: "Subnet",
        width: 150,
        attributes: {
          "aria-controls": "DataTable",
          "aria-label": "Description",
        },
      },
      {
        label: "VLAN ID",
        field: "VlanID",
        sort: "asc",
        width: 270,
      },
      {
        label: "VLAN Name",
        field: "VlanName",
        sort: "asc",
        width: 200,
      },
      {
        label: "MX IP",
        field: "MX_IP",
        sort: "asc",
        width: 100,
      },
      {
        label: "DNS Servers",
        field: "DNS",
        sort: "asc",
        width: 100,
      },
      {
        label: "Network",
        field: "Network",
        sort: "asc",
        width: 100,
      },
    ],
    rows: mapRows,
  };
  return (
    <div id="page-inner-main-templates">
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            <div className="panel-body">
              <div className="panel-group" id="accordion">
                <div className="panel panel-default">
                  <div className="panel-heading">
                    <h4 className="panel-title-description">
                      <a
                        data-toggle="collapse"
                        data-parent="#accordion"
                        href="#collapseOne"
                        className="collapsed"
                      >
                        <span className="glyphicon glyphicon-info-sign"></span>
                      </a>
                    </h4>
                  </div>
                  <div id="collapseOne" className="panel-collapse collapse">
                    <div className="panel-body">
                      This scripts iterates through all networks in an
                      organization and print all the subnets and VLANs
                      associated with every organization. The script works only
                      on MX and Z3 devices, does not work on VPN HUBs, the
                      network must be reachable in the Meraki Dashboard.
                    </div>
                  </div>
                </div>
              </div>
              <button
                className="btn btn-primary"
                onClick={!loading ? handleOrganizationSubnets : null}
                disabled={loading}
              >
                {loading && (
                  <i
                    className="fa fa-refresh fa-spin"
                    style={{ marginRight: "5px" }}
                  />
                )}
                {loading && <span>Loading Data</span>}
                {!loading && <span>RUN</span>}
              </button>

              {/* <a
                  href="#null"
                  className="btn btn-primary"
                  onClick={handleResults}
                >
                  Show results
                </a> */}
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            {showtable ? (
              <div className="panel-body">
                <MDBDataTableV5
                  hover
                  entriesOptions={[10, 25, 50, 100]}
                  entries={10}
                  pagesAmount={10}
                  data={datatable}
                  pagingTop
                  searchTop
                  searchBottom={false}
                  exportToCSV={true}
                />
              </div>
            ) : (
              <div></div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
