import React, { useEffect, useState, useRef } from "react";

export default function LiveLog() {
  const [trigger, settrigger] = useState(0);

  useEffect(() => {
    async function APICallLog() {
      try {
        const logUrl = "./test.txt";
        fetch(logUrl)
          .then((r) => r.text())
          .then((t) => console.log(t));
        // fetch("./test.txt")
        //   .then((response) => {
        //     return response.text();
        //   })
        //   .then((data) => {
        //     console.log(data);
        //     document.getElementById("log").innerHTML = data;
        //   });
      } catch (err) {
        if (err) {
          console.log(err);
        }
      }
    }
    APICallLog();
  }, [trigger]);

  return (
    <div>
      <button onClick={() => settrigger(trigger + 1)}>Log</button>
      <div id="log"></div>
    </div>
  );
}
