import React, { useEffect, useState, useRef } from "react";
import { MDBDataTableV5 } from "mdbreact";

export default function NetworkTopUsers(ac) {
  const [showtable, setshowtable] = useState(false);
  const [trigger, settrigger] = useState(0);
  // const [alert, setalert] = useState(false);
  const [ROW1, setROW1] = useState([]);
  const [ROW2, setROW2] = useState([]);
  const [ROW3, setROW3] = useState([]);
  const [mapROW1, setmapROW1] = useState([]);
  console.log("NetworkTopUsers -> mapROW1", mapROW1);
  const [mapROW2, setmapROW2] = useState([]);
  const [mapROW3, setmapROW3] = useState([]);

  const APIbody2 = {
    "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
    "X-CSRFToken": "frollo",
    ARG_ORGNAME: `${ac.dc.organization}`,
    SERIAL_NUM: `${ac.dc.SNtopUsers}`,
    NET_ID: `${ac.dc.networkID}`,
    NET_NAME: `${ac.dc.network}`,
  };

  const isFirstRun = useRef(true);
  useEffect(() => {
    if (isFirstRun.current) {
      isFirstRun.current = false;
      return;
    }
    async function APIcall() {
      try {
        ac.dc.setloadingButton(true);

        fetch("/topuserdata/", {
          method: ["POST"],
          cache: "no-cache",
          headers: {
            content_type: "application/json",
          },
          body: JSON.stringify(APIbody2),
        }).then((response) => {
          return response.json;
        });

        fetch("/get_report/")
          .then((res) => res.json())
          .then((reports) => {
            ac.dc.setreports(reports.reports);
          })

          .then(() => {
            let R_obj1 = {};
            for (var x = 0; x < ac.dc.reports.length; x++) {
              R_obj1[x] = ac.dc.reports[x];
              console.log("i run");
            }
            console.log("APIcall -> R_obj1", R_obj1);
            setROW1(R_obj1[0]);
            setROW2(R_obj1[1]);
            setROW3(R_obj1[2]);

            let R1 = [];
            // eslint-disable-next-line
            ROW1.map((item) => {
              var rowModel = [
                {
                  TotalusagekB: item[0],
                  DownloadkB: item[1],
                  UploadkB: item[2],
                  Description: item[3],
                  DHCPhostname: item[4],
                  MACaddress: item[5],
                  IPaddress: item[6],
                  VLAN: item[7],
                },
              ];
              R1.push(...rowModel);
              setmapROW1(R1);
            });

            let R2 = [];
            // eslint-disable-next-line
            ROW2.map((item) => {
              var rowModel = [
                {
                  TotalusagekB: item[0],
                  DownloadkB: item[1],
                  UploadkB: item[2],
                  Description: item[3],
                  DHCPhostname: item[4],
                  MACaddress: item[5],
                  IPaddress: item[6],
                  VLAN: item[7],
                },
              ];
              R2.push(...rowModel);
              setmapROW2(R2);
            });

            let R3 = [];
            // eslint-disable-next-line
            ROW3.map((item) => {
              var rowModel = [
                {
                  TotalusagekB: item[0],
                  DownloadkB: item[1],
                  UploadkB: item[2],
                  Description: item[3],
                  DHCPhostname: item[4],
                  MACaddress: item[5],
                  IPaddress: item[6],
                  VLAN: item[7],
                },
              ];
              R3.push(...rowModel);
              setmapROW3(R3);
            });
          })
          .then(() => {
            if (
              mapROW1.length === 0 ||
              mapROW2.length === 0 ||
              mapROW3.length === 0
            ) {
              console.log("APIcall -> mapROW1.length", mapROW1.length);
              console.log("APIcall -> mapROW2.length", mapROW2.length);
              console.log("APIcall -> mapROW3.length", mapROW3.length);
              settrigger(trigger + 1);
            }
          })
          .then(() => {
            if (mapROW1.length > 0) {
              ac.dc.setloadingButton(false);
            }
          })
          .then(() => {
            if (mapROW1.length > 0) {
              setshowtable(true);
            }
          });
      } catch (err) {
        if (err) {
          console.log(err);
        }
      }
    }
    APIcall();
  }, [trigger]);

  const handleTopUsers = (e) => {
    e.preventDefault();
    settrigger(trigger + 1);
  };

  const datatable_10 = {
    columns: [
      {
        label: "Total usage kB",
        field: "TotalusagekB",
        width: 150,
        attributes: {
          "aria-controls": "DataTable",
          "aria-label": "Description",
        },
      },
      {
        label: "Download kB",
        field: "DownloadkB",
        sort: "asc",
        width: 270,
      },
      {
        label: "Upload kB",
        field: "UploadkB",
        sort: "asc",
        width: 200,
      },
      {
        label: "Description",
        field: "Description",
        sort: "asc",
        width: 100,
      },
      {
        label: "DHCP hostname",
        field: "DHCPhostname",
        sort: "asc",
        width: 100,
      },
      {
        label: "MAC address",
        field: "MACaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "IP address",
        field: "IPaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "VLAN",
        field: "VLAN",
        sort: "asc",
        width: 100,
      },
    ],
    rows: mapROW1,
  };
  const datatable_30 = {
    columns: [
      {
        label: "Total usage kB",
        field: "TotalusagekB",
        width: 150,
        attributes: {
          "aria-controls": "DataTable",
          "aria-label": "Description",
        },
      },
      {
        label: "Download kB",
        field: "DownloadkB",
        sort: "asc",
        width: 270,
      },
      {
        label: "UploadkB",
        field: "VlanName",
        sort: "asc",
        width: 200,
      },
      {
        label: "Description",
        field: "Description",
        sort: "asc",
        width: 100,
      },
      {
        label: "DHCP hostname",
        field: "DHCPhostname",
        sort: "asc",
        width: 100,
      },
      {
        label: "MAC address",
        field: "MACaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "IP address",
        field: "IPaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "VLAN",
        field: "VLAN",
        sort: "asc",
        width: 100,
      },
    ],
    rows: mapROW2,
  };

  const datatable_60 = {
    columns: [
      {
        label: "Total usage kB",
        field: "TotalusagekB",
        width: 150,
        attributes: {
          "aria-controls": "DataTable",
          "aria-label": "Description",
        },
      },
      {
        label: "Download kB",
        field: "DownloadkB",
        sort: "asc",
        width: 270,
      },
      {
        label: "UploadkB",
        field: "VlanName",
        sort: "asc",
        width: 200,
      },
      {
        label: "Description",
        field: "Description",
        sort: "asc",
        width: 100,
      },
      {
        label: "DHCP hostname",
        field: "DHCPhostname",
        sort: "asc",
        width: 100,
      },
      {
        label: "MAC address",
        field: "MACaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "IP address",
        field: "IPaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "VLAN",
        field: "VLAN",
        sort: "asc",
        width: 100,
      },
    ],
    rows: mapROW3,
  };

  return (
    <div id="page-inner-main-templates">
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            <div className="panel-body">
              <div className="panel-group" id="accordion">
                <div className="panel panel-default">
                  <div className="panel-heading">
                    <h4 className="panel-title-description">
                      <a
                        data-toggle="collapse"
                        data-parent="#accordion"
                        href="#collapseOne"
                        className="collapsed"
                      >
                        Description
                      </a>
                    </h4>
                  </div>
                  <div id="collapseOne" className="panel-collapse collapse">
                    <div className="panel-body">
                      This is script gets the top 10 heaviest bandwidth users of
                      an MX security appliance for the last 10, 30 and 60
                      minutes.
                    </div>
                  </div>
                </div>
              </div>
              <div className="d-flex"></div>
            </div>
            <div className="panel-body">
              <button
                id="runButton"
                className="btn btn-primary"
                onClick={!ac.dc.loadingButton ? handleTopUsers : null}
                disabled={ac.dc.loadingButton}
              >
                {ac.dc.loadingButton && (
                  <i
                    className="fa fa-refresh fa-spin"
                    style={{ marginRight: "5px" }}
                  />
                )}
                {ac.dc.loadingButton && <span>Loading Data</span>}
