import React, { useEffect, useState } from "react";
import { MDBDataTableV5 } from "mdbreact";

export default function GetAllSubnets(ac) {
  console.log("GetAllSubnets -> ac", ac);
  const [showtable, setshowtable] = useState(false);
  const [trigger, settrigger] = useState(false);
  const [loading, setloading] = useState(false);
  const [alert, setalert] = useState(false);
  const APIbody = {
    "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
    organizationId: `${ac.dc.organizationID}`,
    networkId: `${ac.dc.networkID}`,
  };

  useEffect(() => {
    async function APIcall() {
      try {
        if (trigger === false) {
          return;
        }
        setloading(true);
        fetch("/vlans", {
          method: ["POST"],
          cache: "no-cache",
          headers: {
            content_type: "application/json",
          },
          body: JSON.stringify(APIbody),
        }).then((response) => {
          return response.json;
        });
        fetch("/vlans")
          .then((res) => res.json())
          .then((vlans) => {
            ac.dc.setvlanList(vlans.vlans);
          })
          .then(() => setloading(false));
      } catch (err) {
        if (err) {
          setalert(true);
        }
      }
    }

    APIcall();
  }, [trigger]);

  const handleResults = (e) => {
    e.preventDefault();
    let row = [];
    ac.dc.vlanList.map((item) => {
      var rowModel = [
        {
          Subnet: item.subnet,
          VlanID: item.id,
          VlanName: item.name,
          MX_IP: item.applianceIp,
          DNS: item.dnsNameservers,
        },
      ];
      row.push(...rowModel);
      ac.dc.setmapRows(row);
    });
    setshowtable(true);
  };

  const handleSubnets = (e) => {
    e.preventDefault();
    settrigger(true);
  };

  const datatable = {
    columns: [
      {
        label: "Subnet",
        field: "Subnet",
        width: 150,
        attributes: {
          "aria-controls": "DataTable",
          "aria-label": "Description",
        },
      },
      {
        label: "VLAN ID",
        field: "VlanID",
        sort: "asc",
        width: 270,
      },
      {
        label: "VLAN Name",
        field: "VlanName",
        sort: "asc",
        width: 200,
      },
      {
        label: "MX IP",
        field: "MX_IP",
        sort: "asc",
        width: 100,
      },
      {
        label: "DNS Servers",
        field: "DNS",
        sort: "asc",
        width: 100,
      },
    ],
    rows: ac.dc.mapRows,
  };
  return (
    <div id="page-inner-main-templates">
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            <div className="panel-body">
              <div className="panel panel-success">
                <div className="panel-heading">Description</div>
                <div className="panel-body">
                  <p>
                    This scripts iterates through all networks in an
                    organization and print all the subnets and VLANs associated
                    with every organization. The script works only on MX and Z3
                    devices, does not work on VPN HUBs, the network must be
                    reachable in the Meraki Dashboard.
                  </p>
                </div>
                <button
                  className="btn btn-primary"
                  onClick={!loading ? handleSubnets : null}
                  disabled={loading}
                >
                  {loading && (
                    <i
                      className="fa fa-refresh fa-spin"
                      style={{ marginRight: "5px" }}
                    />
                  )}
                  {loading && <span>Loading Data</span>}
                  {!loading && <span>RUN</span>}
                </button>

                <a
                  href="#null"
                  className="btn btn-primary"
                  onClick={handleResults}
                >
                  Show results
                </a>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            {showtable ? (
              <div className="panel-body">
                <MDBDataTableV5
                  hover
                  entriesOptions={[10, 25, 50, 100]}
                  entries={10}
                  pagesAmount={10}
                  data={datatable}
                  pagingTop
                  searchTop
                  searchBottom={false}
                  exportToCSV={true}
                />
              </div>
            ) : (
              <div></div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
