import React, { useEffect, useState } from "react";
import { MDBDataTableV5 } from "mdbreact";
import Select from "react-select";

export default function NetworkTopUsers(ac) {
  const [showtable, setshowtable] = useState(false);
  const [trigger, settrigger] = useState(0);
  const [alert, setalert] = useState(false);

  const APIbody2 = {
    "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
    "X-CSRFToken": "frollo",
    ARG_ORGNAME: `${ac.dc.organization}`,
    SERIAL_NUM: `${ac.dc.SNtopUsers}`,
    NET_ID: `${ac.dc.networkID}`,
    NET_NAME: `${ac.dc.network}`,
  };

  useEffect(() => {
    async function APICallReports() {
      try {
        // if (trigger === false) {
        //   return;
        // }
        // ac.dc.setloadingButton(true);

        fetch("/find_ports", {
          method: ["POST"],
          cache: "no-cache",
          headers: {
            content_type: "application/json",
          },
          body: JSON.stringify(APIbody2),
        }).then((response) => {
          console.log("APICallReports -> response.json", response.json);
          return response.json;
        });
        fetch("/find_ports/")
          .then((res) => res.json())
          .then((ports) => {
            console.log("APICallReports -> ports", ports);
          });

        //   .then(() => ac.dc.setloadingButton(false));
      } catch (err) {
        if (err) {
          console.log(err);
        }
      }
    }
    APICallReports();
  }, [trigger]);

  //   useEffect(() => {
  //     async function APICallReports() {
  //       try {
  //         // if (trigger === false) {
  //         //   return;
  //         // }
  //         document.getElementById("resultButton").style.visibility = "hidden";
  //         ac.dc.setloadingButton(true);

  //         fetch("/get_report/")
  //           .then((res) => res.json())
  //           .then((reports) => {
  //             ac.dc.setreports(reports.reports);
  //           })
  //           .then(() => ac.dc.setloadingButton(false))
  //           .then(
  //             () =>
  //               (document.getElementById("runButton").style.visibility = "hidden")
  //           )
  //           .then(
  //             () =>
  //               (document.getElementById("resultButton").style.visibility =
  //                 "visible")
  //           );
  //       } catch (err) {
  //         if (err) {
  //           setalert(true);
  //         }
  //       }
  //     }

  //     APICallReports();
  //   }, [trigger]);

  // const ORGANIZATIONS = ac.dc.organizationList.map((opt, index) => ({
  //   label: opt.name,
  //   value: index,
  //   id: opt.id,
  // }));

  // const NETWORKS = ac.dc.networkList.map((opt, index) => ({
  //   label: opt.name,
  //   value: index,
  //   id: opt.id,
  // }));

  const handleTopUsers = (e) => {
    e.preventDefault();
    settrigger(trigger + 1);
  };

  const HandleOrganization = (opt) => {
    ac.dc.setorganization(opt.label);
    ac.dc.setorganizationID(opt.id);
    ac.dc.setnetwork("Networks");
  };

  const HandleNetwork = (opt) => {
    ac.dc.setnetwork(opt.label);
    ac.dc.setnetworkID(opt.id);
  };

  return (
    <div id="page-inner-main-templates">
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            <div className="panel-body">
              <div className="panel-group" id="accordion">
                <div className="panel panel-default">
                  <div className="panel-heading">
                    <h4 className="panel-title-description">
                      <a
                        data-toggle="collapse"
                        data-parent="#accordion"
                        href="#collapseOne"
                        className="collapsed"
                      >
                        Description
                      </a>
                    </h4>
                  </div>
                  <div id="collapseOne" className="panel-collapse collapse">
                    <div className="panel-body">
                      This script finds all MS switchports that match the input
                      search parameter, searching either by clients from a file
                      listing MAC addresses (one per line), a specific tag in
                      Dashboard currently applied to ports, or the specific
                      access policy currently configured.
                    </div>
                  </div>
                </div>
              </div>
              <div className="d-flex">
                {/* <Select
                  options={ORGANIZATIONS}
                  placeholder={ac.dc.organization}
                  onChange={HandleOrganization}
                />
                <Select
                  options={NETWORKS}
                  placeholder={ac.dc.network}
                  onChange={HandleNetwork}
                /> */}
                {/* <Select options={DEVICES} placeholder="Select Device" /> */}
              </div>
            </div>
            <div className="panel-body">
              <button
                id="runButton"
                className="btn btn-primary"
                onClick={!ac.dc.loadingButton ? handleTopUsers : null}
                disabled={ac.dc.loadingButton}
              >
                {ac.dc.loadingButton && (
                  <i
                    className="fa fa-refresh fa-spin"
                    style={{ marginRight: "5px" }}
                  />
                )}
                {ac.dc.loadingButton && <span>Loading Data</span>}
                {!ac.dc.loadingButton && <span>RUN</span>}
              </button>

              <button
                id="resultButton"
                href="#null"
                className="btn btn-primary"
                // onClick={handleResults}
              >
                Show results
              </button>
            </div>
            {/* </div> */}
          </div>
        </div>
      </div>
    </div>
  );
}
