import React, { useEffect } from "react";
import { MDBDataTableV5 } from "mdbreact";

export default function GetAllOrganizationSubnets(ac) {
  const APIbody = {
    "X-Cisco-Meraki-API-Key": `${ac.dc.apiKey}`,
    organizationId: `${ac.dc.organizationID}`,
    networkId: `${ac.dc.networkID}`,
  };

  useEffect(() => {
    async function AllSubnets() {
      const response = await fetch(
        "https://dashboard.meraki.com/api/v0/networks/L_681169443639788368/vlans",
        {
          method: ["POST"],
          cache: "no-cache",
          headers: {
            content_type: "application/json",
          },
          body: JSON.stringify(APIbody),
        }
      );
      const data = await response.json();
      console.log("AllSubnets -> data", data);
    }
  }, []);

  //   const test = (e) => {
  //     e.preventDefault();

  //     for (var x = 0; x < ac.dc.allNetworksIDList.length; x++) {
  //       let row = [];
  //       ac.dc.vlanList.map((item) => {
  //         var rowModel = [
  //           {
  //             Subnet: item.subnet,
  //             VlanID: item.id,
  //             VlanName: item.name,
  //             MX_IP: item.applianceIp,
  //             DNS: item.dnsNameservers,
  //           },
  //         ];
  //         row.push(...rowModel);
  //         //   ac.dc.setmapRows(row);
  //       });
  //     }
  //   };

  const handleOrganizationSubnets = (e) => {
    e.preventDefault();
    let row = [];
    ac.dc.vlanList.map((item) => {
      var rowModel = [
        {
          Subnet: item.subnet,
          VlanID: item.id,
          VlanName: item.name,
          MX_IP: item.applianceIp,
          DNS: item.dnsNameservers,
        },
      ];
      row.push(...rowModel);
      ac.dc.setmapRows(row);
    });
  };

  //   useEffect(() => {
  //     // for (var x = 0; x < ac.dc.allNetworksIDList.length; x++) {
  //     fetch("/allVlans", {
  //       method: ["POST"],
  //       cache: "no-cache",
  //       headers: {
  //         content_type: "application/json",
  //       },
  //       body: JSON.stringify(APIbody),
  //     }).then((response) => {
  //       return response.json;
  //     });

  //     fetch("/allVlans")
  //       .then((res) => res.json())
  //       .then((allVlans) => {
  //         //   ac.dc.setvlanList(vlans.vlans);
  //         console.log("allVlans.allVlans", allVlans.allVlans);
  //       });
  //     // }
  //   }, [ac.dc.networkID]);

  //   const handleOrganizationSubnets = (e) => {
  //     e.preventDefault();
  //     let row = [];
  //     ac.dc.vlanList.map((item) => {
  //       var rowModel = [
  //         {
  //           Subnet: item.subnet,
  //           VlanID: item.id,
  //           VlanName: item.name,
  //           MX_IP: item.applianceIp,
  //           DNS: item.dnsNameservers,
  //         },
  //       ];
  //       row.push(...rowModel);
  //       ac.dc.setmapRows(row);
  //     });
  //   };

  const datatable = {
    columns: [
      {
        label: "Subnet",
        field: "Subnet",
        width: 150,
        attributes: {
          "aria-controls": "DataTable",
          "aria-label": "Description",
        },
      },
      {
        label: "VLAN ID",
        field: "VlanID",
        sort: "asc",
        width: 270,
      },
      {
        label: "VLAN Name",
        field: "VlanName",
        sort: "asc",
        width: 200,
      },
      {
        label: "MX IP",
        field: "MX_IP",
        sort: "asc",
        width: 100,
      },
      {
        label: "DNS Servers",
        field: "DNS",
        sort: "asc",
        width: 100,
      },
    ],
    rows: ac.dc.mapRows,
  };
  return (
    <div id="page-inner-main-templates">
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            <div className="panel-body">
              <div className="panel panel-success">
                <div className="panel-heading">Description</div>
                <div className="panel-body">
                  <p>
                    This scripts iterates through all networks in an
                    organization and print all the subnets and VLANs associated
                    with every organization. The script works only on MX and Z3
                    devices, does not work on VPN HUBs, the network must be
                    reachable in the Meraki Dashboard.
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            <div className="panel-body">
              {/* <button onClick={test}>Button</button> */}
              <a
                href="#null"
                className="btn btn-primary"
                onClick={handleOrganizationSubnets}
              >
                RUN
              </a>
              <MDBDataTableV5
                hover
                entriesOptions={[10, 25, 50, 100]}
                entries={10}
                pagesAmount={10}
                data={datatable}
                pagingTop
                searchTop
                searchBottom={false}
                exportToCSV={true}
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
