import React, { useState } from "react";
import LoginAPI from "./LoginAPI";
import Dashboard from "./Dashboard";
import LoggedIn from "./LoggedIn";
import Logout from "./Logout";
// import ChangeApiKey from "./ChangeApiKey";
import "../styles/Template.css";

export default function Template(ac) {
  const ORG = Object.values(ac.dc.organizationList);

  const routeLoginAPI = () => {
    if (ac.dc.isLoggedIn === false) {
      ac.dc.setclassLogin("active-menu");
      ac.dc.setclassDashboard("");
      ac.dc.setswitchLoggedout(false);
      ac.dc.setswitchLoginAPI(true);
      ac.dc.setswitchDashboard(false);
    } else if (ac.dc.isLoggedIn === true) {
      ac.dc.setswitchLoggedout(true);
      ac.dc.setswitchLoginAPI(false);
      ac.dc.setswitchDashboard(false);
    }
  };

  const routeDashboard = () => {
    // if (ac.dc.isLoggedIn === true) {
    ac.dc.setswitchLoggedout(false);
    ac.dc.setswitchDashboard(true);
    ac.dc.setswitchLoginAPI(false);
    ac.dc.setswitchswitchChangeApiKey(false);
    ac.dc.setclassDashboard("active-menu");
    ac.dc.setclassLogin("");
    // } else {
    //   alert("Please Log-in");
    // }
  };

  const HandleOrganization = (i) => {
    console.log("HandleOrganization -> i", i.target.getAttribute("data-key"));
    ac.dc.setorganization(i.target.getAttribute("key"));
  };

  // const selectOrganization = () => {
  //   ac.dc.organizationList.map((item) => {
  //     console.log(item.name);

  //     // ac.dc.setorganization(item.name);
  //     // ac.dc.setorganizationID(item.id);
  //   });
  // };

  return (
    <div id="wrapper">
      <nav className="navbar navbar-default top-navbar" role="navigation">
        <div className="navbar-header">
          <button
            type="button"
            className="navbar-toggle"
            data-toggle="collapse"
            data-target=".sidebar-collapse"
          >
            <span className="sr-only">Toggle navigation</span>
            <span className="icon-bar"></span>
            <span className="icon-bar"></span>
            <span className="icon-bar"></span>
          </button>
          <a className="navbar-brand" href="index.html">
            <strong>
              <i className="icon fa fa-plane"></i> BRILLIANT
            </strong>
          </a>

          <div id="sideNav" href="">
            <i className="fa fa-bars icon"></i>
          </div>
        </div>

        <ul className="nav navbar-top-links navbar-right">
          <li className="dropdown">Signed in as: {ac.dc.Name}</li>
          <li className="dropdown">
            <a
              className="dropdown-toggle"
              data-toggle="dropdown"
              href="/"
              aria-expanded="false"
            >
              <a href="/">
                {/* <i className="fa fa-sign-out fa-fw"></i> Logout */}
              </a>
            </a>
          </li>
        </ul>
      </nav>
      <nav className="navbar-default navbar-side" role="navigation">
        <div className="sidebar-collapse">
          <ul className="nav" id="main-menu">
            <li>
              <a className={ac.dc.classLogin} onClick={routeLoginAPI}>
                <i className="fa fa-desktop"></i> {ac.dc.logInlogOut}
              </a>
            </li>
            <div className="select-organization">
              <p>ORGANIZATION</p>
            </div>
            <li>
              <a href="/">
                <i class="fa fa-sitemap"></i> Organization
                <span class="fa arrow"></span>
              </a>
              <ul class="nav nav-second-level">
                {ORG.map((item) => {
                  return (
                    <li onClick={HandleOrganization} key={item.id}>
                      <a data-key={item.id}>{item.name}</a>
                    </li>
                  );
                })}
              </ul>
            </li>
            <div className="select-network">
              <p>NETWORK</p>
            </div>
            <li>
              <a href="#">
                <i class="fa fa-sitemap"></i> Network
                <span class="fa arrow"></span>
              </a>
              <ul class="nav nav-second-level">
                <li>
                  <a href="chart.html">Charts JS</a>
                </li>
                <li>
                  <a href="morris-chart.html">Morris Chart</a>
                </li>
              </ul>
            </li>
            <li>
              <a className={ac.dc.classDashboard} onClick={routeDashboard}>
                <i className="fa fa-dashboard"></i> Dashboard
              </a>
            </li>
          </ul>
        </div>
      </nav>
      <div id="page-wrapper">
        <div className="header">
          <h1 className="page-header">
            <small>Welcome to {ac.dc.organization}</small>
          </h1>
          <ol className="breadcrumb">
            <li>
              <a href="/">Home</a>
            </li>
            <li>
              <a href="/">Dashboard</a>
            </li>
            <li className="active">Data</li>
          </ol>
        </div>

        {ac.dc.switchLoginAPI ? <LoginAPI dc={ac.dc} /> : <div></div>}
        {ac.dc.switchDashboard ? <Dashboard dc={ac.dc} /> : <div></div>}
        {ac.dc.switchLoggedIn ? <LoggedIn dc={ac.dc} /> : <div></div>}
        {ac.dc.switchLoggedout ? <Logout dc={ac.dc} /> : <div></div>}
        {/* {ac.dc.switchChangeApiKey ? <ChangeApiKey dc={ac.dc} /> : <div></div>} */}
      </div>
    </div>
  );
}
