        width: 100,
      },
      {
        label: "DHCP hostname",
        field: "DHCPhostname",
        sort: "asc",
        width: 100,
      },
      {
        label: "MAC address",
        field: "MACaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "IP address",
        field: "IPaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "VLAN",
        field: "VLAN",
        sort: "asc",
        width: 100,
      },
    ],
    rows: mapROW2,
  };

  const datatable_60 = {
    columns: [
      {
        label: "Total usage kB",
        field: "TotalusagekB",
        width: 150,
        attributes: {
          "aria-controls": "DataTable",
          "aria-label": "Description",
        },
      },
      {
        label: "Download kB",
        field: "DownloadkB",
        sort: "asc",
        width: 270,
      },
      {
        label: "UploadkB",
        field: "VlanName",
        sort: "asc",
        width: 200,
      },
      {
        label: "Description",
        field: "Description",
        sort: "asc",
        width: 100,
      },
      {
        label: "DHCP hostname",
        field: "DHCPhostname",
        sort: "asc",
        width: 100,
      },
      {
        label: "MAC address",
        field: "MACaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "IP address",
        field: "IPaddress",
        sort: "asc",
        width: 100,
      },
      {
        label: "VLAN",
        field: "VLAN",
        sort: "asc",
        width: 100,
      },
    ],
    rows: mapROW3,
  };

  return (
    <div id="page-inner-main-templates">
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            <div className="panel-body">
              <div className="panel-group" id="accordion">
                <div className="panel panel-default">
                  <div className="panel-heading">
                    <h4 className="panel-title-description">
                      <a
                        data-toggle="collapse"
                        data-parent="#accordion"
                        href="#collapseOne"
                        className="collapsed"
                      >
                        Description
                      </a>
                    </h4>
                  </div>
                  <div id="collapseOne" className="panel-collapse collapse">
                    <div className="panel-body">
                      This is script gets the top 10 heaviest bandwidth users of
                      an MX security appliance for the last 10, 30 and 60
                      minutes.
                    </div>
                  </div>
                </div>
              </div>
              <div className="d-flex">
                <Select
                  options={ORGANIZATIONS}
                  placeholder={ac.dc.organization}
                  onChange={HandleOrganization}
                />
                <Select
                  options={NETWORKS}
                  placeholder={ac.dc.network}
                  onChange={HandleNetwork}
                />
                {/* <Select options={DEVICES} placeholder="Select Device" /> */}
              </div>
            </div>
            <div className="panel-body">
              <button
                className="btn btn-primary"
                // onClick={!ac.dc.loadingButton ? handleTopUsers : null}
                disabled={ac.dc.loadingButton}
              >
                {ac.dc.loadingButton && (
                  <i
                    className="fa fa-refresh fa-spin"
                    style={{ marginRight: "5px" }}
                  />
                )}
                {ac.dc.loadingButton && <span>Loading Data</span>}
                {!ac.dc.loadingButton && <span>RUN</span>}
              </button>
              {/* <button
                className="btn btn-primary"
                onClick={!ac.dc.loadingButton ? handleIndex : null}
                disabled={ac.dc.loadingButton}
              >
                {ac.dc.loadingButton && (
                  <i
                    className="fa fa-refresh fa-spin"
                    style={{ marginRight: "5px" }}
                  />
                )}
                {ac.dc.loadingButton && <span>index</span>}
                {!loadingButton && <span>RUN</span>}
              </button> */}

              {/* <a
                href="#null"
                className="btn btn-primary"
                onClick={handleResults}
              >
                Show results
              </a> */}
            </div>
            {/* </div> */}
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-xs-12">
          <div className="panel panel-default">
            {showtable ? (
              <div>
                <div className="panel-body">
                  <MDBDataTableV5
                    hover
                    entriesOptions={[10]}
                    entries={10}
                    pagesAmount={10}
                    data={datatable_10}
                    pagingTop
                    searchTop
                    searchBottom={false}
                    exportToCSV={true}
                  />
                </div>
                <div className="panel-body">
                  <MDBDataTableV5
                    hover
                    entriesOptions={[10]}
                    entries={10}
                    pagesAmount={10}
                    data={datatable_30}
                    pagingTop
                    searchTop
                    searchBottom={false}
                    exportToCSV={true}
                  />
                </div>
                <div className="panel-body">
                  <MDBDataTableV5
                    hover
                    entriesOptions={[10]}
                    entries={10}
                    pagesAmount={10}
                    data={datatable_60}
                    pagingTop
                    searchTop
                    searchBottom={false}
                    exportToCSV={true}
                  />
                </div>
              </div>
            ) : (
              <div></div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
