  // useEffect(() => {
  //   if (isLoggedIn) {
  //     localStorage.setItem("my-token", JSON.stringify(apiKey));
  //   }
  // });

  const isFirstRunOrg = useRef(true);

  useEffect(() => {
    // if (isFirstRunOrg.current) {
    //   isFirstRunOrg.current = false;
    //   return;
    // }
    async function callOrganization() {
      fetch("/organizations", {
        method: ["POST"],
        cache: "no-cache",
        headers: {
          content_type: "application/json",
        },
        body: JSON.stringify(APIbody),
      }).then((response) => {
        return response.json;
      });
      fetch("/organizations")
        .then((res) => {
          if (!res.ok) {
            console.log("Unable to contact Meraki Cloud");
          }
          setgetOrgStatusCode(res.status);
          return res.json();
        })
        // .then((res) => res.json())
        .then((organizations) => {
          setorganizationList(organizations.organizations);
        })
        .catch((err) => {
          // setalert(true);
          console.log("this is the err: ", err);
        });
    }
    callOrganization();
    return () => {
      setalert(false);
      setAlertModalError([]);
    };
    // eslint-disable-next-line
  }, []);
  // }, [triggerGetOrg]);

  const isFirstRunNetwork = useRef(true);
  useEffect(() => {
    if (isFirstRunNetwork.current) {
      isFirstRunNetwork.current = false;
      return;
    }
    async function callNetworks() {
      fetch("/networks", {
        method: ["POST"],
        cache: "no-cache",
        headers: {
          content_type: "application/json",
        },
        body: JSON.stringify(APIbody),
      }).then((response) => {
        return response.json;
      });
      fetch("/networks")
        // .then((res) => res.json())
        .then((res) => {
          if (!res.ok) {
            throw res;
          }
          return res.json();
        })
        .then((network) => {
          const NET = Object.values(network.networks);
          let networkIDList = [];
          // eslint-disable-next-line
          NET.map((item) => {
            var IDListModel = [
              {
                id: item.id,
              },
            ];
            networkIDList.push(...IDListModel);
            setallNetworksIDList(networkIDList);
          });

          setnetworkList(network.networks);
          settimeZone(network.networks[0].timeZone);
        })
        .catch((err) => {
          err.json().then((errorMessage) => {
            setAlertModalError(errorMessage);
            setswitchAlertModal(true);
          });
          // setalert(true);
          console.log("this is the err: ", err);
        });
      return () => {
        setalert(false);
        setAlertModalError([]);
      };
    }
    callNetworks();

    // eslint-disable-next-line
  }, [organization]);

  const isFirstRunDevices = useRef(true);
  useEffect(() => {
    if (isFirstRunDevices.current) {
      isFirstRunDevices.current = false;
      return;
    }
    async function callDevices() {
      fetch("/devices", {
        method: ["POST"],
        cache: "no-cache",
        headers: {
          content_type: "application/json",
        },
        body: JSON.stringify(APIbody),
      }).then((response) => {
        return response.json;
      });
      fetch("/devices")
        .then((res) => res.json())
        .then((device) => {
          console.log("callDevices -> device", device.devices);
          setdeviceList(device.devices);
          settotalDevices(device.devices.length);
        })
        .catch((err) => {
          setalert(true);
          console.log("this is the error: ", err);
        });
      return () => {
        setalert(false);
      };
    }
    callDevices();

    // eslint-disable-next-line
  }, [network]);

  // const isFirstRunClients = useRef(true);
  // useEffect(() => {
  //   if (isFirstRunClients.current) {
  //     isFirstRunClients.current = false;
  //     return;
  //   }
  //   async function callClients() {
  //     fetch("/clients/", {
  //       method: ["POST"],
  //       cache: "no-cache",
  //       headers: {
  //         content_type: "application/json",
  //       },
  //       body: JSON.stringify(APIbody),
  //     }).then((response) => {
  //       return response.json;
  //     });
  //     fetch("/clients/")
  //       .then((res) => res.json())
  //       .then((device) => {
  //         setdeviceList(device.devices);
  //         settotalDevices(device.devices.length);
  //       })
  //       .catch((err) => {
  //         setalert(true);
  //         console.log("this is the error: ", err);
  //       });
  //     return () => {
  //       setalert(false);
  //     };
  //   }
  //   callClients();

  //   // eslint-disable-next-line
  // }, [network]);

  // useEffect(() => {
  //   if (isOrgSelected && isNetSelected === true) {
  //     let Dev1 = {};
  //     for (var device = 0; device < deviceList.length; device++) {
  //       Dev1[device] = deviceList[device];
  //       let model = Dev1[device].model;
  //       if (model.startsWith("MX") || model.startsWith("Z")) {
  //         setSNtopUsers(Dev1[device].serial);
  //       }
  //     }
  //   }
  //   // eslint-disable-next-line
  // }, [deviceList, network]);

  const dc = {
    triggerGetOrg,
    settriggerGetOrg,
    getOrgStatusCode,
    setgetOrgStatusCode,
    organizationList,
    setorganizationList,
    networkList,
    setnetworkList,
    networkID,
    setnetworkID,
    organization,
    setorganization,
    organizationID,
    setorganizationID,
    timeZone,
    settimeZone,
    network,
    setnetwork,
    apiKey,
    setapiKey,
    inputKey,
    setinputKey,
    inputConfKey,
    setinputConfKey,
    isLoggedIn,
    setisLoggedIn,
    switchLoginAPI,
    setswitchLoginAPI,
    switchDashboard,
    setswitchDashboard,
    switchLoggedIn,
    setswitchLoggedIn,
    switchLoggedout,
    setswitchLoggedout,
    switchToolsTemplate,
    setswitchToolsTemplate,
    classLogin,
    setclassLogin,
    classDashboard,
    setclassDashboard,
    switchswitchChangeApiKey,
    setswitchswitchChangeApiKey,
    switchMainTools,
    setswitchMainTools,
    switchAlertModal,
    setswitchAlertModal,
    logInlogOut,
    setlogInlogOut,
    ulClassorg,
    setulClassorg,
    ulClassnet,
    setulClassnet,
    classOrganization,
    setclassOrganization,
    classNetwork,
    setclassNetwork,
    totalDevices,
    settotalDevices,
    deviceList,
    setdeviceList,
    classToolsTemplate,
    setclassToolsTemplate,
    clientList,
    setclientList,
    vlanList,
    setvlanList,
    allNetworksIDList,
    setallNetworksIDList,
    allVlanList,
    setallVlanList,
    reports,
    setreports,
    datab,
    setdatab,
    triggerTopReports,
    settriggerTopReports,
    loadingButton,
    setloadingButton,
    SNtopUsers,
    setSNtopUsers,
    isOrgSelected,
    setisOrgSelected,
    isNetSelected,
    setisNetSelected,
    alert,
    setalert,
    AlertModalError,
    setAlertModalError,
  };

  return (
    <MainContext.Provider dc={dc}>
