import React, { useState } from "react";
import GetAllDevicesIP from "./Tools/GetAllDevicesIP";
import GetAllSubnets from "./Tools/GetAllSubnets";
import "../styles/Tools.css";

export default function Tools(ac) {
  const [switchGetAllDevicesIP, setswitchGetAllDevicesIP] = useState(false);
  const [switchGetAllSubnets, setswitchGetAllSubnets] = useState(false);
  const handleIPs = (e) => {
    e.preventDefault();
    let row = [];
    ac.dc.clientList.map((item) => {
      var rowModel = [
        {
          Description: item.name,
          Model: item.model,
          LAN_IP_address: item.lanIp,
          MAC_address: item.mac,
          WAN_1_IP: item.wan1Ip,
          WAN_2_IP: item.wan2Ip,
          Serial: item.serial,
        },
      ];
      row.push(...rowModel);
      ac.dc.setmapRows(row);
    });
  };

  const handleSubnets = (e) => {
    e.preventDefault();
    let row = [];
    ac.dc.vlanList.map((item) => {
      var rowModel = [
        {
          Subnet: item.subnet,
          VlanID: item.id,
          VlanName: item.name,
          MX_IP: item.applianceIp,
          DNS: item.dnsNameservers,
        },
      ];
      row.push(...rowModel);
      ac.dc.setmapRows(row);
      console.log("handleSubnets -> row", row);
    });
  };

  return (
    <div id="page-inner">
      <div className="row">
        <div className="col-md-3 col-sm-12 col-xs-12">
          <div className="board">
            <div className="panel panel-primary">
              <div className="number">
                <h3 className="h3-dashboard">{ac.dc.network}</h3>
                <small>Network</small>
              </div>
              <div className="icon">
                <i className="fa fa-eye fa-5x red"></i>
              </div>
            </div>
          </div>
        </div>

        <div className="col-md-3 col-sm-12 col-xs-12">
          <div className="board">
            <div className="panel panel-primary">
              <div className="number">
                <h3 className="h3-dashboard">{ac.dc.organizationID}</h3>
                <small>Organization ID</small>
              </div>
              <div className="icon">
                <i className="fa fa-shopping-cart fa-5x blue"></i>
              </div>
            </div>
          </div>
        </div>

        <div className="col-md-3 col-sm-12 col-xs-12">
          <div className="board">
            <div className="panel panel-primary">
              <div className="number">
                <h3 className="h3-dashboard">{ac.dc.timeZone}</h3>
                <small>Time Zone</small>
              </div>
              <div className="icon">
                <i className="fa fa-comments fa-5x green"></i>
              </div>
            </div>
          </div>
        </div>

        <div className="col-md-3 col-sm-12 col-xs-12">
          <div className="board">
            <div className="panel panel-primary">
              <div className="number">
                <h3 className="h3-dashboard">{ac.dc.totalDevices}</h3>
                <small>Total Devices</small>
              </div>
              <div className="icon">
                <i className="fa fa-user fa-5x yellow"></i>
              </div>
            </div>
          </div>
        </div>
        <div className="col-xs-12">
          <div className="panel panel-default">
            <div className="panel-body">
              <div className="sub-title-tools">Select Tools</div>
              <div>
                <select className="btn btn-default dropdown-toggle-tools">
                  <optgroup label="Tshoot Tools">
                    <option className="option-tools-disabled" value="d">
                      Select Tools
                    </option>
                    <option value="d">Get all devices IPs</option>
                    <option value="d">Get all subnets</option>
                    <option value="d">Top 10 talkers</option>
                  </optgroup>
                </select>
                <div>
                  <div className="panel panel-success">
                    <div className="panel-heading">Description</div>
                    <div className="panel-body">
                      <p>
                        This scripts iterates through all networks in an
                        organization and returns all the IPs associated with
                        every organization.
                      </p>
                    </div>
                  </div>
                </div>
                <a
                  href="#null"
                  className="btn btn-primary"
                  onClick={handleSubnets}
                >
                  RUN
                </a>
              </div>
            </div>
          </div>
        </div>
      </div>
      {ac.dc.switchGetAllDevicesIP ? (
        <GetAllDevicesIP dc={ac.dc} />
      ) : (
        <div></div>
      )}
      {ac.dc.switchGetAllSubnets ? <GetAllSubnets dc={ac.dc} /> : <div></div>}
      {/* <GetAllDevicesIP dc={ac.dc} />
      <GetAllSubnets dc={ac.dc} /> */}
    </div>
  );
}
