import React, { useEffect, useState, useRef } from "react";

export default function LiveLog() {
  const [trigger, settrigger] = useState(0);

  const showFile = () => {
    const file = "./test.txt";
    document.getElementById("log").innerHTML = file;
  };

  return (
    <div>
      <button onClick={showFile}>Log</button>
      <div id="log"></div>
    </div>
  );
}
